import React, { useState, useEffect } from "react";
import {
  Button,
  Card,
  Col,
  Container,
  Spinner,
  FormControl,
  Dropdown,
  ListGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newTaskInput, setNewTaskInput] = useState("");
  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");

  const { data: tasksData, isLoading: tasksLoading } = useQuery(
    "tasks",
    async () => {
      const { data } = await API.get("/tasks");
      return data;
    }
  );

  const { data: categoriesData } = useQuery("categories", async () => {
    const { data } = await API.get("/categories");
    return data;
  });

  const [addCategory] = useMutation(
    async (category) => {
      await API.post("/category", { name: category });
      setCategories([...categories, { name: category }]);
    },
    {
      onSuccess: () => {
        setNewCategoryInput("");
      },
    }
  );

  const [addTask] = useMutation(
    async (task) => {
      await API.post("/task", task);
      setTasks([...tasks, task]);
    },
    {
      onSuccess: () => {
        setNewTaskInput("");
      },
    }
  );

  const [updateTask] = useMutation(async ({ id, text }) => {
    await API.patch(`/task/${id}`, { text });
    setTasks(tasks.map((task) => (task._id === id ? { ...task, text } : task)));
  });

  const [deleteTask, { isLoading: deleteTaskLoading }] = useMutation(
    async (taskId) => {
      await API.delete(`/task/${taskId}`);
    }
  );

  const handleDeleteTask = (id) => {
    deleteTask(id);
    setTasks(tasks.filter((task) => task._id !== id));
  };

  useEffect(() => {
    if (tasksData) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  useEffect(() => {
    if (categoriesData) {
      setCategories(categoriesData);
    }
  }, [categoriesData]);

  const handleTaskAdd = () => {
    if (!newTaskInput) {
      return;
    }

    const task = {
      text: newTaskInput,
      category: selectedCategory,
      done: false,
    };

    addTask(task);
  };

  const handleTaskUpdate = (id, newText) => {
    if (!newText) {
      return;
    }

    updateTask({ id, text: newText });
  };

  const handleCategoryAdd = () => {
    if (!newCategoryInput) {
      return;
    }

    addCategory(newCategoryInput);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const categoryColor = [
    "primary",
    "secondary",
    "success",
    "danger",
    "warning",
    "info",
    "light",
    "dark",
  ];
  return (
    <Container className="my-5">
      <Row className="mb-3">
        <Col>
          <h1>Todo List</h1>
        </Col>
      </Row>
      <Row className="mb-3">
        <Col xs={12} md={4} lg={3}>
          <Card>
            <Card.Body>
              <h5>Add a Task</h5>

              <FormControl
                className="my-3"
                placeholder="Task description"
                value={newTaskInput}
                onChange={(e) => setNewTaskInput(e.target.value)}
              />

              <Dropdown className="my-3">
                <Dropdown.Toggle variant="secondary">
                  {selectedCategory}
                </Dropdown.Toggle>

                <Dropdown.Menu>
                  <Dropdown.Item
                    onClick={() => handleCategoryChange("All Tasks")}
                  >
                    All Tasks
                  </Dropdown.Item>

                  {categories.map((category, index) => (
                    <Dropdown.Item
                      key={category._id}
                      onClick={() => handleCategoryChange(category.name)}
                    >
                      <span
                        className={`text-${
                          categoryColor[index % categoryColor.length]
                        }`}
                      >
                        &#9679;
                      </span>{" "}
                      {category.name}
                    </Dropdown.Item>
                  ))}
                </Dropdown.Menu>
              </Dropdown>

              <Button onClick={handleTaskAdd}>Add Task</Button>
            </Card.Body>
          </Card>

          <Card className="mt-3">
            <Card.Body>
              <h5>Add a Category</h5>

              <FormControl
                className="my-3"
                placeholder="Category name"
                value={newCategoryInput}
                onChange={(e) => setNewCategoryInput(e.target.value)}
              />

              <Button onClick={handleCategoryAdd}>Add Category</Button>
            </Card.Body>
          </Card>
        </Col>

        <Col xs={12} md={8} lg={9}>
          <Card>
            <Card.Body>
              {tasksLoading ? (
                <Spinner animation="border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </Spinner>
              ) : (
                <>
                  <h5>Tasks</h5>

                  <ListGroup>
                    {tasks
                      .filter(
                        (task) =>
                          selectedCategory === "All Tasks" ||
                          task.category === selectedCategory
                      )
                      .map((task) => (
                        <Task
                          key={task._id}
                          task={task}
                          onDelete={() => handleDeleteTask(task._id)}
                          onUpdate={(newText) =>
                            handleTaskUpdate(task._id, newText)
                          }
                          deleteTaskLoading={deleteTaskLoading}
                        />
                      ))}
                  </ListGroup>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
