import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const { data: categoriesData = [] } = useQuery("categories", fetchCategories);
  const { data: tasksData = [] } = useQuery("tasks", fetchTasks);

  useEffect(() => {
    if (categoriesData.length) {
      setCategories(["All Tasks", ...categoriesData]);
    }
  }, [categoriesData]);

  useEffect(() => {
    if (tasksData.length) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  const [addTaskMutation] = useMutation(() => addTask(newTaskInput), {
    onSuccess: (data) => {
      setTasks([...tasks, data]);
      setNewTaskInput("");
    },
  });

  const [updateTaskMutation] = useMutation(updateTask, {
    onSuccess: (data) => {
      setTasks(
        tasks.map((task) => {
          if (task._id === data._id) {
            return data;
          }
          return task;
        })
      );
    },
  });

  const [deleteTaskMutation] = useMutation(deleteTask, {
    onSuccess: (data) => {
      setTasks(tasks.filter((task) => task._id !== data._id));
    },
  });

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleBadge = (id) => {
    if (tasks.find((task) => task._id === id)) {
      updateTaskMutation({
        id,
        category: selectedCategory,
        text: newTaskInput,
        done: !tasks.find((task) => task._id === id).done,
      });
    } else {
      addTaskMutation({ category: selectedCategory, text: newTaskInput });
    }
    setNewTaskInput("");
  };

  const handleTaskDelete = (id) => {
    deleteTaskMutation(id);
  };

  const handleTaskUpdate = (task) => {
    updateTaskMutation({
      id: task._id,
      category: task.category,
      text: task.text,
    });
  };

  return (
    <>
      <Container>
        <Row>
          <Col>
            <h1 className="text-center my-5">To Do List</h1>
          </Col>
        </Row>
        <Row>
          <Col md={4}>
            <Card className="mb-4">
              <Card.Body>
                <Card.Title>
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="New Category"
                      value={newCategoryInput}
                      onChange={(e) => setNewCategoryInput(e.target.value)}
                    />
                    <Button
                      variant="outline-secondary"
                      onClick={handleCategoryAdd}
                    >
                      +
                    </Button>
                  </InputGroup>
                </Card.Title>
                <hr />
                {categories.map((category, index) => (
                  <div key={index}>
                    <FormCheck
                      type="radio"
                      id={`category-${index}`}
                      label={category}
                      checked={selectedCategory === category}
                      onChange={() => handleCategoryChange(category)}
                    />
                    <Button
                      variant="link"
                      size="sm"
                      className="text-danger float-right"
                      onClick={() => handleCategoryDelete(category)}
                    >
                      <CloseSquareOutlined />
                    </Button>
                  </div>
                ))}
              </Card.Body>
            </Card>
          </Col>
          <Col md={8}>
            <Card>
              <Card.Body>
                <Card.Title>
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="New Task"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button
                      variant="outline-secondary"
                      onClick={() => handleBadge(null)}
                    >
                      +
                    </Button>
                  </InputGroup>
                </Card.Title>
                <hr />
                {tasks
                  .filter(
                    (task) =>
                      selectedCategory === "All Tasks" ||
                      task.category === selectedCategory
                  )
                  .map((task, index) => (
                    <div key={index} className="d-flex align-items-center mb-3">
                      <FormCheck
                        type="checkbox"
                        checked={task.done}
                        onChange={() => handleBadge(task._id)}
                        className={`text-${categoryColor[index % 4].color}`}
                      />
                      <div className="mx-3">
                        <FormControl
                          plaintext={!task.editing}
                          defaultValue={task.text}
                          onChange={(e) =>
                            handleTaskUpdate({ ...task, text: e.target.value })
                          }
                        />
                        <Button
                          variant="link"
                          size="sm"
                          className="text-success"
                          onClick={() =>
                            setTasks(
                              tasks.map((t) =>
                                t._id === task._id
                                  ? { ...t, editing: !t.editing }
                                  : t
                              )
                            )
                          }
                        >
                          <FormOutlined />
                        </Button>
                        <Button
                          variant="link"
                          size="sm"
                          className="text-danger"
                          onClick={() => handleTaskDelete(task._id)}
                        >
                          <DeleteOutlined />
                        </Button>
                      </div>
                    </div>
                  ))}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
}

const fetchCategories = async () => {
  try {
  const response = await API.get("/categories");
  return response.data.data.categories;
  } catch (error) {
  console.log(error);
  }
  };

  const fetchTasks = async () => {
  try {
  const response = await API.get("/tasks");
  return response.data.data.tasks;
  } catch (error) {
  console.log(error);
  }
  };

  const addTask = async (data) => {
  try {
  const response = await API.post("/task", data);
  return response.data.data.task;
  } catch (error) {
  console.log(error);
  }
  };

  const updateTask = async (data) => {
  try {
  const response = await API.patch(`/task/${data.id}`, data);
  return response.data.data.task;
  } catch (error) {
  console.log(error);
  }
  };

  const deleteTask = async (id) => {
  try {
  const response = await API.delete(`/task/${id}`);
  return response.data.data.task;
  } catch (error) {
  console.log(error);
  }
  };
