import React, { useState } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API, setAuthToken } from "../../config/api";
import { useMutation, useQuery } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const { isLoading, error, data, refetch } = useQuery("getTasks", async () => {
    const response = await API.get("/tasks");
    return response.data.data.tasks;
  });

  const { isLoading: isFetchingCategories, data: categoriesData, refetch: refetchCategories } = useQuery(
    "categories",
    async () => {
      const response = await API.get("/categories");
      return response.data.data.categories;
    },
    {
      refetchOnWindowFocus: false,
    }
  );

  const { mutate: addTask, isLoading: isAddingTask } = useMutation(
    async (newTask) => {
      await API.post("/task", newTask);
    },
    {
      onSuccess: () => {
        refetch();
        setNewTaskInput("");
      },
    }
  );

  const { mutate: updateTask, isLoading: isUpdatingTask } = useMutation(
    async (task) => {
      const { _id, ...rest } = task;
      await API.put(`task/${_id}`, rest);
    },
    {
      onSuccess: () => {
        refetch();
      },
    }
  );

  const { mutate: deleteTask, isLoading: isDeletingTask } = useMutation(
    async (id) => {
      await API.delete(`tasks/${id}`);
    },
    {
      onSuccess: () => {
        refetch();
      },
    }
  );

  const { mutate: addCategory, isLoading: isAddingCategory } = useMutation(
    async (newCategory) => {
      await API.post("category", newCategory);
    },
    {
      onSuccess: (data) => {
        refetchCategories();
        setSelectedCategory(data.data.category.name);
      },
    }
  );

  const handleBadge = (task) => {
    if (tasks.find((t) => t._id === task._id)) {
      setTasks(
        tasks.map((t) => {
          if (t._id === task._id) {
            return { ...t, done: !t.done };
          }
          return t;
        })
      );
      updateTask(task);
    } else {
      const newTasks = tasks.concat(task);
      setTasks(newTasks);
      setNewTaskInput("");
      addTask(task);
    }
  };

  const categoryColor = [
    {
      index: 0,
      color: "danger",
    },
    {
      index: 1,
      color: "success",
    },
    {
      index: 2,
      color: "warning",
    },
    {
      index: 3,
      color: "info",
    },
  ];

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task._id !== id));
    deleteTask(id);
  };

  const handleTaskUpdate = (task) => {
    setTasks(
      tasks.map((t) => {
        if (t._id === task._id) {
          return task;
        }
        return t;
      })
    );
    updateTask(task);
  };

  return (
    <Container>
      <Row>
        <Col>
          <Card>
            <Card.Body>
              <Card.Title className="mb-3">Todo List</Card.Title>
              <Row>
                <Col>
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="Add new task..."
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button
                      variant="outline-secondary"
                      disabled={isAddingTask}
                      onClick={() =>
                        handleBadge({
                          name: newTaskInput,
                          category: selectedCategory,
                          done: false,
                        })
                      }
                    >
                      Add
                    </Button>
                  </InputGroup>
                </Col>
              </Row>
              <Row>
                <Col>
                  <div className="mb-3">
                    {categories.map((category, index) => (
                      <Badge
                        key={category}
                        pill
                        bg={categoryColor[index % 4].color}
                        className="me-2 mb-2"
                        onClick={() => handleCategoryChange(category)}
                        style={{
                          cursor: "pointer",
                          textDecoration:
                            category === selectedCategory
                              ? "underline"
                              : "none",
                        }}
                      >
                        {category}
                        <DeleteOutlined
                          className="ms-1"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleCategoryDelete(category);
                          }}
                        />
                      </Badge>
                    ))}
                  </div>
                </Col>
                <Col>
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="Add new category..."
                      value={newCategoryInput}
                      onChange={(e) => setNewCategoryInput(e.target.value)}
                    />
                    <Button
                      variant="outline-secondary"
                      onClick={() => handleCategoryAdd()}
                    >
                      Add
                    </Button>
                  </InputGroup>
                </Col>
              </Row>
              <Row>
                <Col>
                  {isLoading ? (
                    <p>Loading...</p>
                  ) : error ? (
                    <p>Error: {error.message}</p>
                  ) : (
                    data
                      .filter(
                        (task) =>
                          selectedCategory === "All Tasks" ||
                          task.category === selectedCategory
                      )
                      .map((task) => (
                        <div key={task._id}>
                          <FormCheck className="mb-2">
                            <FormCheck.Input
                              type="checkbox"
                              checked={task.done}
                              onChange={() => handleBadge(task)}
                            />
                            <FormLabel
                              className={
                                task.done ? "text-decoration-line-through" : ""
                              }
                            >
                              {task.name}
                            </FormLabel>
                          </FormCheck>
                          <FormOutlined
                            className="me-2"
                            onClick={() => handleTaskUpdate(task)}
                          />
                          <CloseSquareOutlined
                            onClick={() => handleTaskDelete(task._id)}
                          />
                        </div>
                      ))
                  )}
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
