import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await API.get("/categories");
        setCategories(["All Tasks", ...res.data.data.categories]);
      } catch (err) {
        console.error(err);
      }
    };
    fetchCategories();
  }, []);

  const handleBadge = (id) => {
    if (tasks.find((task) => task?._id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task._id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      const newTasks = tasks.concat({
        _id: id,
        category: selectedCategory,
        text: newTaskInput,
        done: false,
      });
      setTasks(newTasks);
      setNewTaskInput("");
    }
  };

  const categoryColors = [
    {
      index: 0,
      bgColor: "danger.500",
    },
    {
      index: 1,
      bgColor: "purple.500",
    },
    {
      index: 2,
      bgColor: "green.500",
    },
    {
      index: 3,
      bgColor: "primary.500",
    },
    {
      index: 4,
      bgColor: "warning.500",
    },
  ];

  const handleCategoryAdd = async () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      try {
        const res = await API.post("/category", { name: newCategory });
        setCategories([...categories, res.data.data.category]);
        setSelectedCategory(newCategory);
        setNewCategoryInput("");
      } catch (err) {
        console.error(err);
      }
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await API.get("/tasks");
        setTasks(res.data.data.tasks);
      } catch (err) {
        console.error(err);
      }
    };
    fetchTasks();
  }, []);

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task?._id !== id));
  };

  const handleTaskEdit = (id) => {
    const updatedTasks = tasks.map((task) => {
      if (task._id === id) {
        return { ...task, text: newTaskInput };
      }
      return task;
    });
    setTasks(updatedTasks);
    setNewTaskInput("");
  };

  const handleClearTasks = () => {
    setTasks([]);
  };

  const filteredTasks =
    selectedCategory === "All Tasks"
      ? tasks
      : tasks.filter((task) => task.category === selectedCategory);

  return (
    <Container>
      <Row className="my-3">
        <Col>
          <Card>
            <Card.Body>
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="New Task"
                  value={newTaskInput}
                  onChange={(e) => setNewTaskInput(e.target.value)}
                />
                <Button
                  variant="primary"
                  onClick={() => handleBadge(Date.now())}
                >
                  Add
                </Button>
              </InputGroup>
              <Row>
                {categories.map((category, index) => (
                  <Col xs={4} key={index}>
                    <Badge
                      variant="light"
                      className="text-capitalize"
                      style={{
                        backgroundColor: categoryColors[index]?.bgColor,
                      }}
                      onClick={() => handleCategoryChange(category)}
                    >
                      {category}
                      {category !== "All Tasks" && (
                        <CloseSquareOutlined
                          className="ms-2"
                          onClick={() => handleCategoryDelete(category)}
                        />
                      )}
                    </Badge>
                  </Col>
                ))}
                <Col xs={4}>
                  <InputGroup>
                    <FormControl
                      placeholder="New Category"
                      value={newCategoryInput}
                      onChange={(e) => setNewCategoryInput(e.target.value)}
                    />
                    <Button
                      variant="success"
                      onClick={() => handleCategoryAdd()}
                    >
                      Add
                    </Button>
                  </InputGroup>
                </Col>
              </Row>
              <hr />
              <Row>
                <Col xs={12}>
                  <h5 className="text-capitalize">{selectedCategory}</h5>
                </Col>
              </Row>
              <hr />
              <Row>
                {filteredTasks.length > 0 ? (
                  filteredTasks.map((task) => (
                    <Col xs={12} key={task._id}>
                      <Card className="mb-3">
                        <Card.Body>
                          <Row>
                            <Col xs={9}>
                              <FormCheck
                                type="checkbox"
                                label={task.text}
                                checked={task.done}
                                onChange={() => handleBadge(task._id)}
                              />
                            </Col>
                            <Col xs={3}>
                              <FormOutlined
                                className="me-2"
                                onClick={() => handleTaskEdit(task._id)}
                              />
                              <DeleteOutlined
                                onClick={() => handleTaskDelete(task._id)}
                              />
                            </Col>
                          </Row>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))
                ) : (
                  <Col xs={12}>
                    <p className="text-center">No tasks found</p>
                  </Col>
                )}
              </Row>
              <hr />
              <Row>
                <Col xs={12}>
                  <Button variant="danger" onClick={() => handleClearTasks()}>
                    Clear Tasks
                  </Button>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
