import React, { useState, useEffect } from "react";
import {
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState();
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const categoryColor = [
    {
      index: 0,
      color: "danger",
    },
    {
      index: 1,
      color: "success",
    },
    {
      index: 2,
      color: "warning",
    },
    {
      index: 3,
      color: "info",
    },
  ];

  const fetchCategories = async () => {
    const { data } = await API.get("/categories");
    setCategories(["All Tasks", ...data.data]);
  };

  const fetchTasks = async () => {
    const { data } = await API.get("/tasks");
    setTasks(data.data);
  };

  const { isLoading, error } = useQuery("categories", fetchCategories);
  const { isLoading: isTasksLoading, error: tasksError } = useQuery(
    "tasks",
    fetchTasks
  );

  const [addTask, { data }] = useMutation(async (task) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const response = await API.post("/task", task, config);
    return response.data;
  });

  useEffect(() => {
    if (data) {
      setTasks([...tasks, data.data]);
    }
  }, [data, setTasks, tasks]);

  const [updateTask] = useMutation(async (task) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const { data } = await API.patch(`/task/${task._id}`, task, config);

    setTasks(tasks.map((t) => (t._id === task._id ? { ...data.data } : t)));
  });

  const [deleteTask] = useMutation(async (id) => {
    await API.delete(`/task/${id}`);

    setTasks(tasks.filter((t) => t._id !== id));
  });

  useEffect(() => {
    if (tasksError) {
      alert("Failed to fetch tasks data");
    }
  }, [tasksError]);

  useEffect(() => {
    if (error) {
      alert("Failed to fetch categories data");
    }
  }, [error]);

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleTaskAdd = () => {
    const newTask = newTaskInput.trim();
    if (newTask !== "") {
      const newTaskData = {
        name: newTask,
        category: selectedCategory,
        isCompleted: false,
      };
      addTask(newTaskData);
      setNewTaskInput("");
    }
  };

  const handleTaskDelete = (id) => {
    deleteTask(id);
  };

  const handleTaskUpdate = (task) => {
    updateTask(task);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col md={4}>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Add Category</Card.Title>
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="Category name"
                  value={newCategoryInput}
                  onChange={(e) => setNewCategoryInput(e.target.value)}
                />
                <Button variant="outline-secondary" onClick={handleCategoryAdd}>
                  Add
                </Button>
              </InputGroup>
            </Card.Body>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Categories</Card.Title>
              <ul className="list-group">
                {categories?.map((category, index) => (
                  <li
                    key={index}
                    className={`list-group-item ${
                      selectedCategory === category && "active"
                    }`}
                    onClick={() => handleCategorySelect(category)}
                  >
                    {category}
                  </li>
                ))}
              </ul>
            </Card.Body>
          </Card>
        </Col>
        <Col md={8}>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Add Task</Card.Title>
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="Task name"
                  value={newTaskInput}
                  onChange={(e) => setNewTaskInput(e.target.value)}
                />
                <Button variant="outline-secondary" onClick={handleTaskAdd}>
                  Add
                </Button>
              </InputGroup>
              <FormLabel>Category</FormLabel>
              <FormCheck
                type="radio"
                label="All Tasks"
                name="category"
                checked={selectedCategory === "All Tasks"}
                onChange={() => handleCategorySelect("All Tasks")}
              />
              {categories.map((category, index) => (
                <FormCheck
                  key={index}
                  type="radio"
                  label={category}
                  name="category"
                  checked={selectedCategory === category}
                  onChange={() => handleCategorySelect(category)}
                />
              ))}
            </Card.Body>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Task List</Card.Title>
              {isTasksLoading ? (
                <p>Loading...</p>
              ) : (
                tasks
                  .filter((task) =>
                    selectedCategory === "All Tasks"
                      ? true
                      : task?.category === selectedCategory
                  )
                  .map((task) => (
                    <Card
                      key={task._id}
                      className="mb-2"
                      border={
                        categoryColor.find(
                          (color) =>
                            color?.index === categories?.indexOf(task.category)
                        ).color
                      }
                    >
                      <Card.Body>
                        <Row>
                          <Col md={9}>
                            <FormCheck
                              type="checkbox"
                              label={task.name}
                              checked={task.isCompleted}
                              onChange={() =>
                                handleTaskUpdate({
                                  ...task,
                                  isCompleted: !task.isCompleted,
                                })
                              }
                            />
                          </Col>
                          <Col md={3}>
                            <Button
                              variant="danger"
                              size="sm"
                              onClick={() => handleTaskDelete(task._id)}
                            >
                              Delete
                            </Button>
                          </Col>
                        </Row>
                      </Card.Body>
                    </Card>
                  ))
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
