import React, { useState } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import "../../style/";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([
    "All Tasks",
    "Favourites",
    "Groceries",
    "Work",
    "Study",
    "Sports",
  ]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const handleBadge = (id) => {
    if (tasks.find((task) => task.id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task.id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      const newTasks = tasks.concat({
        id,
        category: selectedCategory,
        text: newTaskInput,
        done: false,
      });
      setTasks(newTasks);
      setNewTaskInput("");
    }
  };
  const categoryColors = {
    "All Tasks": "primary",
    Favourites: "secondary",
    Groceries: "success",
    Work: "danger",
    Study: "warning",
    Sports: "info",
  };

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const handleTaskUpdate = (id, newText) => {
    setTasks(
      tasks.map((task) => {
        if (task.id === id) {
          return { ...task, text: newText };
        }
        return task;
      })
    );
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card className="todo-card">
          <Container className="text-center">
            <Row>
              <Col xs={5}>
                <div className="text-start m-3"></div>
              </Col>
              <Col xs={7}>
                <InputGroup className="mb-3">
                  <FormControl
                    placeholder="Add new task"
                    aria-label="Add new task"
                    aria-describedby="basic-addon2"
                    value={newTaskInput}
                    onChange={(e) => setNewTaskInput(e.target.value)}
                  />
                  <Button
                    variant="outline-primary"
                    id="button-addon2"
                    onClick={() => handleBadge(Date.now())}
                  >
                    Add Task
                  </Button>
                </InputGroup>
              </Col>
            </Row>
            <Row>
              <Col xs={5}>
                <div className="text-start m-3">Categories:</div>
                {categories.map((category, index) => (
                  <div key={index} className="text-start m-3">
                    <FormCheck
                      inline
                      label={category}
                      type="radio"
                      name="category"
                      checked={category === selectedCategory}
                      onChange={() => handleCategoryChange(category)}
                    />
                    {category !== "All Tasks" && (
                      <DeleteOutlined
                        className="mx-2"
                        onClick={() => handleCategoryDelete(category)}
                      />
                    )}
                  </div>
                ))}
                <div className="text-start m-3">
                  <InputGroup>
                    <FormControl
                      placeholder="Add new category"
                      aria-label="Add new category"
                      value={newCategoryInput}
                      onChange={(e) => setNewCategoryInput(e.target.value)}
                    />
                    <Button
                      variant="outline-primary"
                      id="button-addon2"
                      onClick={() => handleCategoryAdd()}
                    >
                      Add
                    </Button>
                  </InputGroup>
                </div>
              </Col>
              <Col xs={7}>
                {tasks
                  .filter(
                    (task) =>
                      selectedCategory === "All Tasks" ||
                      task.category === selectedCategory
                  )
                  .map((task) => (
                    <Card key={task.id} className="mb-3">
                      <Card.Body>
                        <InputGroup>
                          <FormCheck
                            type="checkbox"
                            checked={task.done}
                            onChange={() => handleBadge(task.id)}
                          />
                          <FormControl
                            aria-label="Task description"
                            value={task.text}
                            onChange={(e) =>
                              handleTaskUpdate(task.id, e.target.value)
                            }
                            disabled={task.done}
                          />
                          {!task.done && (
                            <Button
                              variant="outline-primary"
                              onClick={() => handleBadge(task.id)}
                            >
                              Done
                            </Button>
                          )}
                          <CloseSquareOutlined
                            className="mx-2"
                            onClick={() => handleTaskDelete(task.id)}
                          />
                        </InputGroup>
                      </Card.Body>
                      <Card.Footer className="text-muted">
                        <Badge bg={categoryColors[task.category]}>
                          {task.category}
                        </Badge>
                      </Card.Footer>
                    </Card>
                  ))}
              </Col>
            </Row>
          </Container>
        </Card>
      </Container>
    </>
  );
}
