import React, { useState } from "react";
import {
  Card,
  Container,
  Row,
  Col,
  InputGroup,
  FormControl,
  Badge,
  Stack,
  FormCheck,
  FormLabel,
  Button,
} from "react-bootstrap";

export default function TodoList() {
  // const [dataTask, setDataTask] = useState([])
  const [idTask, setIdTask] = useState([]);

  function handleBadge(id) {
    let idNow = idTask.filter((e) => e === id);
    if (idNow[0] !== id) {
      setIdTask([...idTask, id]);
    } else {
      setIdTask(idTask.filter((e) => e !== id));
    }
  }
  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={3}>
                <div className="text-start m-3">
                  <p className="fw-bold">All Tasks</p>
                  <p>Favourites</p>
                  <p>Groceries</p>
                  <p>Work</p>
                  <p>Study</p>
                  <p>Sports</p>
                  <p className="opacity-50">+ New category</p>
                </div>
              </Col>

              <Col xs={9}>
                <div className="p-3">
                  <p className="text-start">TODO LIST</p>
                  <InputGroup className="mb-2">
                    <FormControl placeholder="Add a new task" />
                  </InputGroup>
                </div>
                <div className="d-flex px-3 my-2 align-content-center">
                  <FormCheck className="me-3" onClick={handleBadge(1)} />
                  <FormLabel className="me-2">Hellow</FormLabel>
                  {toping.filter((e) => e === element.id)[0] === element.id ? (
                    <Badge
                      pill
                      bg="success"
                      className="rounded-circle d-flex justify-content-center align-items-center"
                      style={{
                        width: "20px",
                        height: "20px",
                        fontSize: "10pt",
                        position: "absolute",
                        right: 30,
                      }}
                    >
                      <FontAwesomeIcon icon={faCheck} />
                    </Badge>
                  ) : (
                    <Badge
                      pill
                      bg="white"
                      className="rounded-circle d-flex justify-content-center align-items-center"
                      style={{
                        width: "20px",
                        height: "20px",
                        fontSize: "10pt",
                        position: "absolute",
                        right: 0,
                      }}
                    >
                      <FontAwesomeIcon icon={faCheck} />
                    </Badge>
                  )}
                  <Button size="sm">Success</Button>
                </div>
              </Col>
            </Row>
          </Container>
        </Card>
      </Container>
    </>
  );
}
