import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  async function fetchCategories() {
    const response = await fetch("/categories");
    const data = await response.json();

    if (data && data.categories) {
      setCategories(data.categories);
    }
  }

  async function fetchTasks() {
    const response = await fetch("/tasks");
    const data = await response.json();

    if (data && data.tasks) {
      setTasks(data.tasks);
    }
  }

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await API.get("/categories");
        setCategories(["All Tasks", ...res.data.data.categories]);
      } catch (err) {
        console.error(err);
      }
    };
    fetchCategories();
  }, []);

  const handleBadge = (id) => {
    if (tasks.find((task) => task?._id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task._id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      const newTasks = tasks.concat({
        _id: id,
        category: selectedCategory,
        text: newTaskInput,
        done: false,
      });
      setTasks(newTasks);
      setNewTaskInput("");
    }
  };

  const categoryColors = [
    {
      index: 0,
      bgColor: "danger.500",
    },
    {
      index: 1,
      bgColor: "purple.500",
    },
    {
      index: 2,
      bgColor: "green.500",
    },
    {
      index: 3,
      bgColor: "primary.500",
    },
    {
      index: 4,
      bgColor: "warning.500",
    },
  ];

  const handleCategoryAdd = async () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      try {
        const res = await API.post("/category", { name: newCategory });
        setCategories([...categories, res.data.data.category]);
        setSelectedCategory(newCategory);
        setNewCategoryInput("");
      } catch (err) {
        console.error(err);
      }
    }
  };

  const handleCategoryDelete = async (category) => {
    try {
      await API.delete(`/category/${category}`);
      setCategories(categories.filter((cat) => cat !== category));
      setSelectedCategory("All Tasks");
    } catch (err) {
      console.error(err);
    }
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await API.get("/tasks");
        setTasks(res.data.data.tasks);
      } catch (err) {
        console.error(err);
      }
    };
    fetchTasks();
  }, []);

  const handleTaskDelete = async (id) => {
    try {
    await API.delete(`/task/${id}`);
    setTasks(tasks.filter((task) => task._id !== id));
    } catch (err) {
    console.error(err);
    }
    };

    const handleTaskEdit = async (id, newText) => {
      try {
      await API.put( `/task/${id}`, { text: newText });
      setTasks(
      tasks.map((task) => {
      if (task._id === id) {
      return { ...task, text: newText };
      }
      return task;
      })
      );
      } catch (err) {
      console.error(err);
      }
      };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={5}>
                <div className="text-start m-3">
                  {categories.map((category) => (
                    <div
                      key={category}
                      className="d-flex align-items-center my-2"
                    >
                      <Badge bg={category.toLowerCase()} className="me-2" />
                      <p
                        className={
                          selectedCategory === category
                            ? "fw-bold mb-0"
                            : "mb-0"
                        }
                        onClick={() => handleCategoryChange(category)}
                      >
                        {category}
                      </p>
                      <CloseSquareOutlined
                        onClick={() => handleCategoryDelete(category)}
                        className="ms-auto"
                      />
                    </div>
                  ))}
                  <div className="d-flex align-items-center my-2">
                    <FormLabel className="opacity-50 me-2">
                      New category:
                    </FormLabel>
                    <InputGroup>
                      <FormControl
                        placeholder="Category name"
                        value={newCategoryInput}
                        onChange={(e) => setNewCategoryInput(e.target.value)}
                      />
                      <FormOutlined onClick={handleCategoryAdd} />
                    </InputGroup>
                  </div>
                </div>
              </Col>

              <Col xs={7}>
                <div className="text-start m-3">
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="Task name"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button
                      variant="primary"
                      onClick={() =>
                        handleBadge({
                          id: tasks.length + 1,
                          category: selectedCategory,
                          text: newTaskInput,
                          done: false,
                        })
                      }
                    >
                      Add Task
                    </Button>
                  </InputGroup>
                  {tasks
                    .filter(
                      (task) =>
                        selectedCategory === "All Tasks" ||
                        task.category === selectedCategory
                    )
                    .map((task) => (
                      <div
                        key={task.id}
                        className="d-flex align-items-center justify-content-between mb-3"
                      >
                        <div className="d-flex align-items-center">
                          <FormCheck
                            type="checkbox"
                            className="me-3"
                            checked={task.done}
                            onChange={() => handleBadge(task.id)}
                          />
                          <FormControl
                            value={task.text}
                            style={
                              task.done
                                ? { textDecoration: "line-through" }
                                : {}
                            }
                            onChange={(e) =>
                              handleTaskUpdate(task.id, e.target.value)
                            }
                          />
                          <Badge
                            bg={categoryColors[task.category]}
                            className="ms-2"
                          >
                            {task.category}
                          </Badge>
                        </div>
                        <DeleteOutlined
                          onClick={() => handleTaskDelete(task.id)}
                        />
                      </div>
                    ))}
                </div>
              </Col>
            </Row>
          </Container>
        </Card>
      </Container>
    </>
  );
}
