import React, { useState, useEffect, useCallback, useMemo } from "react";
import { useQuery } from "react-query";
import { CloseSquareOutlined, FormOutlined } from "@ant-design/icons";
import {
  Form,
  Spinner,
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const {
    isLoading: isLoadingCategories,
    data: categoriesData,
    isError: isErrorCategories,
  } = useQuery("categories", async () => {
    const response = await API.get("/categories");
    return response.data.categories;
  });

  useEffect(() => {
    if (categoriesData && categoriesData.length > 0) {
      setCategories(categoriesData);
      setSelectedCategory(categoriesData[0].name);
    }
  }, [categoriesData]);

  // Fetch tasks
  const {
    isLoading: isLoadingTasks,
    data: tasksData,
    isError: isErrorTasks,
  } = useQuery("tasks", async () => {
    const response = await API.get("/tasks");
    return response.data.tasks;
  });

  useEffect(() => {
    if (tasksData) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  const handleBadge = useCallback(
    async (id) => {
      const task = tasks.find((task) => task._id === id);
      if (task) {
        const updatedTask = { ...task, done: !task.done };
        const response = await API.put(`task/${id}`, updatedTask);
        if (response.status === 200) {
          setTasks((tasks) =>
            tasks.map((task) => (task._id === id ? updatedTask : task))
          );
        }
      } else {
        const newTask = {
          category: selectedCategory.name,
          name: newTaskInput,
          done: false,
        };
        const response = await API.post("/task", newTask);
        if (response.status === 201) {
          const createdTask = response.data.task;
          setTasks((tasks) => [...tasks, createdTask]);
          setNewTaskInput("");
        }
      }
    },
    [tasks, selectedCategory, newTaskInput]
  );

  const categoryColors = useMemo(
    () => [
      {
        index: 0,
        bgColor: "danger.500",
      },
      {
        index: 1,
        bgColor: "purple.500",
      },
      {
        index: 2,
        bgColor: "green.500",
      },
      {
        index: 3,
        bgColor: "primary.500",
      },
      {
        index: 4,
        bgColor: "warning.500",
      },
    ],
    []
  );

  const handleCategoryAdd = useCallback(async () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      try {
        const response = await API.post("/category", { name: newCategory });
        const createdCategory = response.data.category;
        setCategories((categories) => [...categories, createdCategory]);
        setSelectedCategory(createdCategory);
        setNewCategoryInput("");
      } catch (err) {
        console.error(err);
      }
    }
  }, [newCategoryInput, categories]);

  const handleCategoryDelete = useCallback(async (categoryId) => {
    try {
      const response = await API.delete(`/category/${categoryId}`);
      if (response.status === 200) {
        setCategories((categories) =>
          categories.filter((category) => category._id !== categoryId)
        );
        setSelectedCategory("All Tasks");
      }
    } catch (err) {
      console.error(err);
    }
  }, []);

  return (
    <Container>
      <Row className="my-4">
        <Col xs={12} sm={4}>
          <Card>
            <Card.Header>
              <FormLabel className="font-weight-bold">Categories</FormLabel>
            </Card.Header>
            <Card.Body>
              {isLoadingCategories ? (
                <Spinner animation="border" />
              ) : isErrorCategories ? (
                <p>Error loading categories</p>
              ) : (
                <>
                  <Button
                    variant="danger"
                    className="mr-2 mb-2"
                    size="sm"
                    onClick={() => handleCategoryDelete(selectedCategory._id)}
                  >
                    <CloseSquareOutlined />
                  </Button>
                  {categories.map((category) => (
                    <Badge
                      key={category._id}
                      variant={
                        selectedCategory._id === category._id
                          ? "primary"
                          : "secondary"
                      }
                      className="mr-2 mb-2"
                      style={{
                        backgroundColor:
                          categoryColors[category.index % 5].bgColor,
                      }}
                      onClick={() => setSelectedCategory(category)}
                    >
                      {category.name}
                    </Badge>
                  ))}
                  <InputGroup>
                    <FormControl
                      placeholder="Add new category"
                      value={newCategoryInput}
                      onChange={(e) => setNewCategoryInput(e.target.value)}
                    />
                    <InputGroup.Append>
                      <Button variant="primary" onClick={handleCategoryAdd}>
                        <FormOutlined />
                      </Button>
                    </InputGroup.Append>
                  </InputGroup>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
        <Col xs={12} sm={8}>
          <Card>
            <Card.Header>
              <FormLabel className="font-weight-bold">
                {selectedCategory.name}
              </FormLabel>
            </Card.Header>
            <Card.Body>
              {isLoadingTasks ? (
                <Spinner animation="border" />
              ) : isErrorTasks ? (
                <p>Error loading tasks</p>
              ) : (
                <>
                  {tasks.length === 0 && <p>No tasks found.</p>}
                  {tasks
                    .filter(
                      (task) =>
                        selectedCategory === "All Tasks" ||
                        task.category === selectedCategory.name
                    )
                    .map((task) => (
                      <Row key={task._id} className="my-2">
                        <Col xs={1} sm={1} md={1}>
                          <Form.Check
                            type="checkbox"
                            checked={task.done}
                            onChange={() => handleBadge(task._id)}
                          />
                        </Col>
                        <Col xs={10} sm={10} md={11}>
                          <p>{task.name}</p>
                        </Col>
                      </Row>
                    ))}
                  <InputGroup>
                    <FormControl
                      placeholder="Add new task"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <InputGroup.Append>
                      <Button variant="primary" onClick={() => handleBadge()}>
                        <FormOutlined />
                      </Button>
                    </InputGroup.Append>
                  </InputGroup>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
