import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API, setAuthToken } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const handleBadge = (id) => {
    if (tasks.find((task) => task._id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task._id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      const newTasks = tasks.concat({
        _id: id,
        category: selectedCategory,
        text: newTaskInput,
        done: false,
      });
      setTasks(newTasks);
      setNewTaskInput("");
    }
  };

  const categoryColor = [
    {
      index: 0,
      color: "danger",
    },
    {
      index: 1,
      color: "success",
    },
    {
      index: 2,
      color: "warning",
    },
    {
      index: 3,
      color: "info",
    },
  ];

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task._id !== id));
  };

  const handleTaskUpdate = (id, newText) => {
    setTasks(
      tasks.map((task) => {
        if (task._id === id) {
          return { ...task, text: newText };
        }
        return task;
      })
    );
  };

  const { data: categoriesData, isLoading: categoriesLoading } = useQuery(
    "categories",
    async () => {
      const response = await API.get("/categories");
      return response.data.data.categories;
    }
  );

  const { data: tasksData, isLoading: tasksLoading } = useQuery(
    "tasks",
    async () => {
      const response = await API.get("/tasks");
      return response.data.data.tasks;
    }
  );

  useEffect(() => {
    setAuthToken(localStorage.getItem("token"));
    if (tasksData) {
      setTasks(tasksData);
    }
    if (categoriesData) {
      setCategories(["All Tasks", ...categoriesData]);
    }
  }, [tasksData, categoriesData]);

  const [deleteTask, { isLoading: deleteTaskLoading }] = useMutation(
    async (taskId) => {
      await API.delete(`/task/${taskId

}`);

const handleDeleteTask = (id) => {
deleteTask(id);
setTasks(tasks.filter((task) => task._id !== id));
};

return (
<Container className="my-5">
<Row>
<Col md={4}>
<Card className="mb-3">
<Card.Body>
<Card.Title>Add a Task</Card.Title>
<FormControl
placeholder="Enter task"
value={newTaskInput}
onChange={(e) => setNewTaskInput(e.target.value)}
/>
<FormLabel className="mt-2">Choose a category</FormLabel>
<InputGroup className="mb-3">
<FormControl
placeholder="Enter category"
value={newCategoryInput}
onChange={(e) => setNewCategoryInput(e.target.value)}
/>
<Button onClick={handleCategoryAdd}>Add</Button>
</InputGroup>
{categories.map((category, index) => (
<FormCheck
key={category}
type="radio"
label={category}
name="category"
checked={selectedCategory === category}
onChange={() => handleCategoryChange(category)}
className={`text-${categoryColor[index % 4].color}`}
/>
))}
</Card.Body>
</Card>
</Col>
<Col md={8}>
<Card className="mb-3">
<Card.Body>
<Card.Title>Tasks</Card.Title>
{tasksLoading ? (
<p>Loading tasks...</p>
) : (
<>
{tasks.length > 0 ? (
tasks
.filter(
(task) =>
selectedCategory === "All Tasks" ||
task.category === selectedCategory
)
.map((task) => (
<div key={task._id} className="mb-3">
<Badge
pill
variant={task.done ? "success" : "danger"}
className="mr-3"
onClick={() => handleBadge(task._id)}
>
{task.done ? "Done" : "Undone"}
</Badge>
{task.text}
<FormOutlined
className="ml-3"
onClick={() => {
const newText = prompt(
"Enter new text for the task",
task.text
);
if (newText !== null) {
handleTaskUpdate(task._id, newText);
}
}}
/>
<DeleteOutlined
className="ml-3"
onClick={() => handleDeleteTask(task._id)}
/>
</div>
))
) : (
<p>No tasks yet!</p>
)}
</>
)}
</Card.Body>
</Card>
{deleteTaskLoading && <p>Deleting task...</p>}
</Col>
</Row>
</Container>
);})}
