import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  useEffect(() => {
    async function fetchCategories() {
      const response = await fetch("/categories");
      const data = await response.json();
      if (data && data.categories) {
        setCategories(data.categories);
      }
    }
    fetchCategories();
  }, []);

  const handleBadge = (id) => {
    if (tasks.find((task) => task._id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task._id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      const newTasks = tasks.concat({
        _id: id,
        category: selectedCategory,
        text: newTaskInput,
        done: false,
      });
      setTasks(newTasks);
      setNewTaskInput("");
    }
  };

  const categoryColors = [
    {
      index: 0,
      bgColor: "danger.500",
    },
    {
      index: 1,
      bgColor: "purple.500",
    },
    {
      index: 2,
      bgColor: "green.500",
    },
    {
      index: 3,
      bgColor: "primary.500",
    },
    {
      index: 4,
      bgColor: "warning.500",
    },
  ];

  const handleCategoryAdd = async () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      try {
        const res = await API.post("/category", { name: newCategory });
        setCategories([...categories, res.data.data.category]);
        setSelectedCategory(newCategory);
        setNewCategoryInput("");
      } catch (err) {
        console.error(err);
      }
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  useEffect(() => {
    async function fetchTasks() {
      const response = await fetch("/tasks");
      const data = await response.json();

      if (data && data.tasks) {
        setTasks(data.tasks);
      }
    }
    fetchTasks();
  }, []);

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task._id !== id));
  };

  const handleTaskUpdate = (id, newText) => {
    setTasks(
      tasks.map((task) => {
        if (task._id === id) {
          return { ...task, text: newText };
        }
        return task;
      })
    );
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row className="my-3">
              <Col>
                <InputGroup className="mb-3">
                  <FormControl
                    placeholder="New Category"
                    aria-label="New Category"
                    aria-describedby="basic-addon2"
                    value={newCategoryInput}
                    onChange={(e) => setNewCategoryInput(e.target.value)}
                  />
                  <InputGroup.Append>
                    <Button
                      variant="outline-secondary"
                      onClick={handleCategoryAdd}
                    >
                      Add Category
                    </Button>
                  </InputGroup.Append>
                </InputGroup>
              </Col>
            </Row>
            <Row>
              <Col>
                {categories.map((category, index) => (
                  <Badge
                    key={index}
                    pill
                    bg={
                      categoryColors[index]
                        ? categoryColors[index].bgColor
                        : "secondary"
                    }
                    text="light"
                    className="mr-2 mb-2"
                    onClick={() => handleCategoryChange(category)}
                  >
                    {category}{" "}
                    <CloseSquareOutlined
                      onClick={(e) => {
                        e.stopPropagation();
                        handleCategoryDelete(category);
                      }}
                    />
                  </Badge>
                ))}
              </Col>
            </Row>
          </Container>
          <Card.Body>
            <Row>
              <Col>
                <FormCheck
                  type="checkbox"
                  label="Show Completed Tasks"
                  checked={selectedCategory === "Completed"}
                  onChange={() =>
                    setSelectedCategory(
                      selectedCategory === "Completed"
                        ? "All Tasks"
                        : "Completed"
                    )
                  }
                />
              </Col>
            </Row>
            <Row className="my-3">
              <Col>
                <InputGroup className="mb-3">
                  <FormControl
                    placeholder="New Task"
                    aria-label="New Task"
                    aria-describedby="basic-addon2"
                    value={newTaskInput}
                    onChange={(e) => setNewTaskInput(e.target.value)}
                  />
                  <InputGroup.Append>
                    <Button
                      variant="outline-secondary"
                      onClick={() => handleBadge(new Date().getTime())}
                    >
                      Add Task
                    </Button>
                  </InputGroup.Append>
                </InputGroup>
              </Col>
            </Row>
            {tasks
              .filter(
                (task) =>
                  selectedCategory === "All Tasks" ||
                  task.category === selectedCategory
              )
              .map((task) => (
                <Card
                  bg="light"
                  key={task._id}
                  className="my-3"
                  border={task.done ? "success" : ""}
                >
                  <Card.Body>
                    <FormCheck
                      type="checkbox"
                      label={task.text}
                      checked={task.done}
                      onChange={() => handleBadge(task._id)}
                    />
                    <Button
                      variant="outline-danger"
                      className="ml-2"
                      onClick={() => handleTaskDelete(task._id)}
                    >
                      <DeleteOutlined />
                    </Button>
                    <Button
                      variant="outline-primary"
                      className="ml-2"
                      onClick={() =>
                        handleTaskUpdate(
                          task._id,
                          prompt("Update Task", task.text)
                        )
                      }
                    >
                      <FormOutlined />
                    </Button>
                  </Card.Body>
                </Card>
              ))}
          </Card.Body>
        </Card>
      </Container>
    </>
  );
}
