import React, { useState } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([
    "All Tasks",
    "Favourites",
    "Groceries",
    "Work",
    "Study",
    "Sports",
  ]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const handleBadge = (id) => {
    if (tasks.find((task) => task.id === id)) {
      setTasks(tasks.filter((task) => task.id !== id));
    } else {
      const newTasks = tasks.concat({
        id,
        category: selectedCategory,
        text: `Task ${id}`,
        completed: false,
      });
      setTasks(newTasks);
    }
  };

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const handleTaskUpdate = (id, newText) => {
    setTasks(
      tasks.map((task) => {
        if (task.id === id) {
          return { ...task, text: newText };
        }
        return task;
      })
    );
  };

  const handleTaskCompletion = (id) => {
    setTasks(
      tasks.map((task) => {
        if (task.id === id) {
          return { ...task, completed: !task.completed };
        }
        return task;
      })
    );
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={5}>
                <div className="text-start m-3">
                  {categories.map((category) => (
                    <div key={category}>
                      <p
                        className={
                          selectedCategory === category ? "fw-bold" : ""
                        }
                        onClick={() => handleCategoryChange(category)}
                      >
                        <Badge
                          bg={
                            category.toLowerCase() === "all tasks"
                              ? "secondary"
                              : category.toLowerCase()
                          }
                          className="me-2"
                        >
                          {category}
                        </Badge>
                        <CloseSquareOutlined
                          onClick={() => handleCategoryDelete(category)}
                        />
                      </p>
                    </div>
                  ))}
                  <div className="d-flex align-items-center">
                    <FormLabel className="opacity-50 me-2">
                      New category:
                    </FormLabel>
                    <InputGroup className="mb-3">
                      <FormControl
                        placeholder="Category name"
                        value={newCategoryInput}
                        onChange={(e) => setNewCategoryInput(e.target.value)}
                        />
                        <Button variant="outline-secondary" onClick={handleCategoryAdd}>
                        Add
                        </Button>
                        </InputGroup>
                        </div>
                        </div>
                        </Col>
                        <Col xs={7}>
                        <div className="text-end m-3">
                        <Form>
                        <InputGroup>
                        <FormControl
                        placeholder="New task"
                        value={newTaskInput}
                        onChange={(e) => setNewTaskInput(e.target.value)}
                        />
                        <Button
                        variant="outline-secondary"
                        onClick={() => handleBadge(Math.random())}
                        >
                        Add
                        </Button>
                        </InputGroup>
                        </Form>
                        </div>
                        <div>
                        {tasks
                        .filter(
                        (task) =>
                        selectedCategory === "All Tasks" ||
                        task.category === selectedCategory
                        )
                        .map((task) => (
                          <Card
                          key={task.id}
                          className={`my-3 ${ task.completed ? "text-decoration-line-through" : "" }`}
                        >
                        <Card.Body>
                        <div className="d-flex justify-content-between align-items-center">
                        <div>
                        <FormCheck
                        type="checkbox"
                        checked={task.completed}
                        onChange={() => handleTaskCompletion(task.id)}
                        />
                        <FormLabel
                        className="ms-2"
                        onClick={() =>
                        handleTaskCompletion(task.id)
                        }
                        >
                        {task.text}
                        </FormLabel>
                        </div>
                        <div>
                        <FormOutlined
                        onClick={() =>
                        handleTaskUpdate(
                        task.id,
                        `Updated Task ${task.id}`
                        )
                        }
                        />
                        <DeleteOutlined
                        className="ms-2"
                        onClick={() => handleTaskDelete(task.id)}
                        />
                        </div>
                        </div>
                        </Card.Body>
                        </Card>
                        ))}
                        </div>
                        </Col>
                        </Row>
                        </Container>
                        </Card>
                        </Container>
                        </>
                        );
                        }
