import React, { useState, useEffect } from "react";
import { useQuery } from "react-query";
import { CloseSquareOutlined, FormOutlined } from "@ant-design/icons";
import {
  Spinner,
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const { isLoading: isLoadingCategories, data: categoriesData } = useQuery(
    "categories",
    async () => {
      const response = await API.get("/categories");
      return response.data.categories;
    }
  );

  useEffect(() => {
    if (categoriesData && categoriesData.length > 0) {
      setCategories(categoriesData);
      setSelectedCategory(categoriesData[0]);
    }
  }, [categoriesData]);

  // Fetch tasks
  const { isLoading: isLoadingTasks, data: tasksData } = useQuery(
    "tasks",
    async () => {
      const response = await API.get("/tasks");
      return response.data.tasks;
    }
  );

  useEffect(() => {
    if (tasksData) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  const handleBadge = async (id) => {
    const task = tasks.find((task) => task._id === id);
    if (task) {
      const updatedTask = { ...task, done: !task.done };
      const response = await API.put(`task/${id}`, updatedTask);
      if (response.status === 200) {
        setTasks((tasks) =>
          tasks.map((task) => (task._id === id ? updatedTask : task))
        );
      }
    } else {
      const newTask = {
        category: selectedCategory,
        text: newTaskInput,
        done: false,
      };
      const response = await API.post("/task", newTask);
      if (response.status === 201) {
        const createdTask = response.data.task;
        setTasks((tasks) => [...tasks, createdTask]);
        setNewTaskInput("");
      }
    }
  };

  const categoryColors = [
    {
      index: 0,
      bgColor: "danger.500",
    },
    {
      index: 1,
      bgColor: "purple.500",
    },
    {
      index: 2,
      bgColor: "green.500",
    },
    {
      index: 3,
      bgColor: "primary.500",
    },
    {
      index: 4,
      bgColor: "warning.500",
    },
  ];

  const handleCategoryAdd = async () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      try {
        const response = await API.post("/category", { name: newCategory });
        const createdCategory = response.data.category;
        setCategories((categories) => [...categories, createdCategory]);
        setSelectedCategory(createdCategory);
        setNewCategoryInput("");
      } catch (err) {
        console.error(err);
      }
    }
  };

  const handleCategoryDelete = async (category) => {
    const response = await API.delete(`category/${category}`);
    if (response.status === 200) {
      setCategories((categories) =>
        categories.filter((cat) => cat !== category)
      );
    }
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = async (id) => {
    const response = await API.delete(`task/${id}`);
    if (response.status === 200) {
      setTasks((tasks) => tasks.filter((task) => task._id !== id));
    }
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={5}>
                <div className="text-start m-3">
                  {categories.map((category) => (
                    <div
                      key={category}
                      className="d-flex align-items-center my-2"
                    >
                      <Badge
                        bg={
                          typeof category === "string"
                            ? category.toLowerCase()
                            : ""
                        }
                        className="me-2"
                      />
                      <p
                        className={
                          selectedCategory === category
                            ? "fw-bold mb-0"
                            : "mb-0"
                        }
                        onClick={() => handleCategoryChange(category)}
                      >
                        {category}
                      </p>
                      <CloseSquareOutlined
                        onClick={() => handleCategoryDelete(category)}
                        className="ms-auto"
                      />
                    </div>
                  ))}
                  <div className="d-flex align-items-center my-2">
                    <FormLabel className="opacity-50 me-2">
                      New category:
                    </FormLabel>
                    <InputGroup>
                      <FormControl
                        placeholder="Category name"
                        value={newCategoryInput}
                        onChange={(e) => setNewCategoryInput(e.target.value)}
                      />
                      <FormOutlined onClick={handleCategoryAdd} />
                    </InputGroup>
                  </div>
                </div>
              </Col>

              <Col xs={7}>
                <div className="text-start m-3">
                  <InputGroup>
                    <FormControl
                      placeholder="Task name"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <FormOutlined onClick={() => handleBadge()} />
                  </InputGroup>
                  <div className="my-3">
                    {tasks
                      .filter(
                        (task) =>
                          selectedCategory === "All Tasks" ||
                          task.category === selectedCategory
                      )
                      .map((task) => (
                        <div
                          key={task._id}
                          className="d-flex align-items-center mb-3"
                        >
                          <Badge
                            bg={
                              categoryColors[
                                categories.findIndex(
                                  (category) => category === task.category
                                )
                              ]
                                ? categoryColors[
                                    categories.findIndex(
                                      (category) => category === task.category
                                    )
                                  ].bgColor
                                : ""
                            }
                            className="me-2"
                          />
                          <p
                            className={
                              task.done
                                ? "text-decoration-line-through mb-0"
                                : "mb-0"
                            }
                            onClick={() => handleBadge(task._id)}
                          >
                            {task.text}
                          </p>
                          <CloseSquareOutlined
                            onClick={() => handleTaskDelete(task._id)}
                            className="ms-auto"
                          />
                        </div>
                      ))}
                    {tasks &&
                      tasks.length === 0 &&
                      "There are no tasks in this category."}
                  </div>
                </div>
              </Col>
            </Row>
          </Container>
        </Card>
      </Container>
    </>
  );
}
