import React, { useState } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  FormSelect,
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
  Spinner,
} from "react-bootstrap";
import { API, setAuthToken } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const { data: categoriesData, isLoading: categoriesLoading } = useQuery(
    "categories",
    async () => {
      const response = await API.get("/categories");
      return response.data.categories;
    }
  );

  const { data: tasksData, isLoading: tasksLoading } = useQuery(
    ["tasks", selectedCategory],
    async () => {
      const response = await API.get(
        `/tasks?category=${selectedCategory.toLowerCase()}`
      );
      return response.data.tasks;
    }
  );

  const addCategoryMutation = useMutation(
    async () => {
      const response = await API.post("/category", {
        name: newCategoryInput,
      });
      return response.data.category;
    },
    {
      onSuccess: (data) => {
        setCategories([...categories, data]);
        setSelectedCategory(data.name);
        setNewCategoryInput("");
      },
    }
  );

  const addTaskMutation = useMutation(
    async (newTaskInput) => {
      const response = await API.post("/task", {
        category: selectedCategory.toLowerCase(),
        name: newTaskInput,
      });
      return response.data.task;
    },
    {
      onSuccess: (data) => {
        setTasks([...tasks, data]);
        setNewTaskInput("");
      },
    }
  );

  const handleBadge = (id) => {
    if (tasks.find((task) => task._id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task._id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      addTaskMutation.mutate(newTaskInput);
    }
  };

  const categoryColors = {
    "All Tasks": "primary",
    Favourites: "secondary",
    Groceries: "success",
    Work: "danger",
    Study: "warning",
    Sports: "info",
  };

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      addCategoryMutation.mutate();
    }
  };

  const handleCategoryDelete = async (category) => {
    try {
      await API.delete(`/category/${category._id}`);
      setCategories(categories.filter((cat) => cat._id !== category._id));
      setSelectedCategory("All Tasks");
    } catch (err) {
      console.log(err);
    }
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = async (id) => {
    try {
      await API.delete(`/task/${id}`);
      setTasks(tasks.filter((task) => task._id !== id));
    } catch (err) {
      console.log(err);
    }
  };

  const handleTaskChange = (id, newName) => {
    setTasks(
      tasks.map((task) => {
        if (task._id === id) {
          return { ...task, name: newName };
        }
        return task;
      })
    );
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={5}>
                <div className="text-start m-3">
                  {categories.map((category) => (
                    <div
                      key={category}
                      className="d-flex align-items-center my-2"
                    >
                      <Badge bg={category.toLowerCase()} className="me-2" />
                      <p
                        className={
                          selectedCategory === category
                            ? "fw-bold mb-0"
                            : "mb-0"
                        }
                        onClick={() => handleCategoryChange(category)}
                      >
                        {category}
                      </p>
                      <CloseSquareOutlined
                        onClick={() => handleCategoryDelete(category)}
                        className="ms-auto"
                      />
                    </div>
                  ))}
                  <div className="d-flex align-items-center my-2">
                    <FormLabel className="opacity-50 me-2">
                      New category:
                    </FormLabel>
                    <InputGroup>
                      <FormControl
                        placeholder="Category name"
                        value={newCategoryInput}
                        onChange={(e) => setNewCategoryInput(e.target.value)}
                      />
                      <FormOutlined onClick={handleCategoryAdd} />
                    </InputGroup>
                  </div>
                </div>
              </Col>

              <Col xs={7}>
                <div className="text-start m-3">
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="Task name"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button
                      variant="primary"
                      onClick={() =>
                        handleBadge({
                          id: tasks.length + 1,
                          category: selectedCategory,
                          text: newTaskInput,
                          done: false,
                        })
                      }
                    >
                      Add Task
                    </Button>
                  </InputGroup>
                  {tasks
                    .filter(
                      (task) =>
                        selectedCategory === "All Tasks" ||
                        task.category === selectedCategory
                    )
                    .map((task) => (
                      <Card key={task._id} className="mb-2 shadow-sm">
                        <Card.Body>
                          <Row>
                            <Col xs={1}>
                              <FormCheck
                                type="checkbox"
                                checked={task.done}
                                onChange={() => handleBadge(task._id)}
                              />
                            </Col>
                            <Col xs={9}>
                              <FormControl
                                value={task.text}
                                onChange={(e) =>
                                  handleTaskChange(task._id, e.target.value)
                                }
                              />
                            </Col>
                            <Col xs={2}>
                              <CloseSquareOutlined
                                onClick={() => handleBadge(task._id)}
                              />
                            </Col>
                          </Row>
                        </Card.Body>
                      </Card>
                    ))}
                </div>
              </Col>
            </Row>
          </Container>
        </Card>
      </Container>
    </>
  );
}
