import React, { useState, useEffect } from "react";
import {
CloseSquareOutlined,
DeleteOutlined,
FormOutlined,
} from "@ant-design/icons";
import {
Button,
Card,
Col,
Container,
FormCheck,
FormControl,
InputGroup,
Row,
Form
} from "react-bootstrap";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
const [tasks, setTasks] = useState([]);
const [categories, setCategories] = useState([]);
const [newCategoryInput, setNewCategoryInput] = useState("");
const [selectedCategory, setSelectedCategory] = useState("All Tasks");
const [newTaskInput, setNewTaskInput] = useState("");
const categoryColor = [
{ index: 0, color: "danger" },
{ index: 1, color: "success" },
{ index: 2, color: "warning" },
{ index: 3, color: "info" },
];
const { data: categoriesData } = useQuery("categories", fetchCategories);
const { data: tasksData } = useQuery("tasks", fetchTasks);

useEffect(() => {
fetchCategories();
fetchTasks();
}, []);

const fetchCategories = async () => {
try {
const response = await API.get("/categories");
setCategories(["All Tasks", ...response.data]);
} catch (error) {
console.log(error);
}
};

const fetchTasks = async () => {
try {
const response = await API.get("/tasks");
setTasks(response.data);
} catch (error) {
console.log(error);
}
};

const [addTaskMutation] = useMutation((data) => API.post("/tasks", data), {
onSuccess: (newTask) => {
setTasks([...tasks, newTask]);
},
});

const [updateTaskMutation] = useMutation(
(data) => API.put(`/task/${data.id}`, data),
{
onSuccess: (updatedTask) => {
setTasks(
tasks.map((task) => {
if (task._id === updatedTask._id) {
return updatedTask;
}
return task;
})
);
},
}
);

const [deleteTaskMutation] = useMutation((id) => API.delete(`/tasks/${id}`), {
onSuccess: (deletedTask) => {
setTasks(tasks.filter((task) => task._id !== deletedTask._id));
},
});

const handleCategoryAdd = () => {
const newCategory = newCategoryInput.trim();
if (newCategory !== "" && !categories.includes(newCategory)) {
setCategories([...categories, newCategory]);
setSelectedCategory(newCategory);
setNewCategoryInput("");
}
};

const handleCategoryDelete = (category) => {
setCategories(categories.filter((cat) => cat !== category));
};

const handleCategoryChange = (category) => {
setSelectedCategory(category);
};

const handleBadge = (id) => {
if (tasks.find((task) => task._id === id)) {
updateTaskMutation({
id,
category: selectedCategory,
text: newTaskInput,
done: !tasks.find((task) => task._id === id).done,
});
} else {
addTaskMutation({ category: selectedCategory, text: newTaskInput });
}
setNewTaskInput("");
};

const handleTaskDelete = (id) => {
deleteTaskMutation(id);
};

const handleTaskUpdate = (id, newText) => {
  updateTaskMutation({
  id,
  text: newText,
  done: tasks.find((task) => task._id === id).done,
  });
  };

  return (
    <Container className="mt-5">
    <Card className="p-4">
    <Card.Title className="text-center">Todo List</Card.Title>
    <Row className="mb-3">

    <Col sm={4}>
    <InputGroup className="mb-3">
    <FormControl
    placeholder="New Task"
    value={newTaskInput}
    onChange={(e) => setNewTaskInput(e.target.value)}
    />
    <Button variant="primary" onClick={() => handleBadge()}>
    Add Task
    </Button>
    </InputGroup>
    </Col>
    <Col sm={4}>
    <InputGroup className="mb-3">
    <FormControl
    placeholder="New Category"
    value={newCategoryInput}
    onChange={(e) => setNewCategoryInput(e.target.value)}
    />
    <Button variant="primary" onClick={() => handleCategoryAdd()}>
    Add Category
    </Button>
    </InputGroup>
    </Col>
    <Col sm={4}>
    <Form.Select
    value={selectedCategory}
    onChange={(e) => handleCategoryChange(e.target.value)}
    >
    {categories.map((category, index) => (
    <option key={index} value={category}>
    {category}
    </option>
    ))}
    </Form.Select>
    </Col>
    </Row>
    {tasksData && tasksData.length > 0 ? (
    tasksData
    .filter((task) =>
    selectedCategory === "All Tasks"
    ? true
    : task.category === selectedCategory
    )
    .map((task, index) => (
    <div key={index}>
    <Row className="mt-2">
    <Col sm={2}>
    <FormCheck
    type="checkbox"
    checked={task.done}
    onChange={() => handleBadge(task._id)}
    />
    </Col>
    <Col sm={8}>
    {task._id === -1 ? (
    <FormControl
    placeholder="Task"
    value={newTaskInput}
    onChange={(e) => setNewTaskInput(e.target.value)}
    />
    ) : (
    <FormControl
    placeholder="Task"
    value={task.text}
    onChange={(e) => handleTaskUpdate(task._id, e.target.value)}
    />
    )}
    </Col>
    <Col sm={2}>
    <CloseSquareOutlined
    onClick={() => handleTaskDelete(task._id)}
    />
    </Col>
    </Row>
    </div>
    ))
    ) : (
    <p className="text-center">No tasks found.</p>
    )}
    </Card>
    </Container>
    );
    }
