import React, { useState, useEffect } from "react";
import { useQuery } from "react-query";
import { CloseSquareOutlined, FormOutlined } from "@ant-design/icons";
import {
  Spinner,
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const { isLoading: isLoadingCategories, data: categoriesData } = useQuery(
    "categories",
    async () => {
      const response = await API.get("/categories");
      return response.data.categories;
    }
  );

  useEffect(() => {
    if (categoriesData && categoriesData.length > 0) {
      setCategories(categoriesData);
      setSelectedCategory(categoriesData[0].name);
    }
  }, [categoriesData]);

  // Fetch tasks
  const { isLoading: isLoadingTasks, data: tasksData } = useQuery(
    "tasks",
    async () => {
      const response = await API.get("/tasks");
      return response.data.tasks;
    }
  );

  useEffect(() => {
    if (tasksData) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  const handleBadge = async (id) => {
    const task = tasks.find((task) => task._id === id);
    if (task) {
      const updatedTask = { ...task, done: !task.done };
      const response = await API.put(`task/${id}`, updatedTask);
      if (response.status === 200) {
        setTasks((tasks) =>
          tasks.map((task) => (task._id === id ? updatedTask : task))
        );
        return tasks; // add this line
      }
    } else {
      const newTask = {
        category: selectedCategory,
        name: newTaskInput,
        done: false,
      };
      const response = await API.post("/task", newTask);
      if (response.status === 201) {
        const createdTask = response.data.task;
        setTasks((tasks) => [...tasks, createdTask]);
        setNewTaskInput("");
        return tasks; // add this line
      }
    }
  };

  const categoryColors = [
    {
      index: 0,
      bgColor: "danger",
    },
    {
      index: 1,
      bgColor: "purple",
    },
    {
      index: 2,
      bgColor: "green",
    },
    {
      index: 3,
      bgColor: "primary",
    },
    {
      index: 4,
      bgColor: "warning",
    },
  ];

  const handleCategoryAdd = async () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      try {
        const response = await API.post("/category", { name: newCategory });
        const createdCategory = response.data.category;
        setCategories((prevCategories) => [...prevCategories, createdCategory]);
        setNewCategoryInput("");
      } catch (error) {
        console.log(error);
      }
    }
  };

  const handleTaskAdd = async () => {
    const newTask = newTaskInput.trim();
    if (newTask !== "") {
      try {
        const response = await API.post("/task", {
          category: selectedCategory,
          name: newTask,
          done: false,
        });
        const createdTask = response.data.task;
        setTasks((prevTasks) => [...prevTasks, createdTask]);
        setNewTaskInput("");
      } catch (error) {
        console.log(error);
      }
    }
  };

  const handleCategorySelect = (categoryName) => {
    setSelectedCategory(categoryName);
  };

  return (
    <Container>
      <Row>
        <Col md={4} className="mb-4">
          <Card>
            <Card.Body>
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="New category name"
                  aria-label="New category name"
                  aria-describedby="basic-addon2"
                  value={newCategoryInput}
                  onChange={(e) => setNewCategoryInput(e.target.value)}
                />
                <InputGroup>
                  <Button
                    variant="outline-secondary"
                    onClick={handleCategoryAdd}
                  >
                    Add
                  </Button>
                </InputGroup>
              </InputGroup>
              {isLoadingCategories ? (
                <Spinner animation="border" />
              ) : (
                <div>
                  <div
                    className={`category-badge ${
                      selectedCategory === "All Tasks" ? "active" : ""
                    }`}
                    onClick={() => handleCategorySelect("All Tasks")}
                  >
                    All Tasks
                  </div>
                  {categories.map((category, index) => (
                    <div
                      key={category._id}
                      className={`category-badge ${
                        selectedCategory === category.name ? "active" : ""
                      }`}
                      style={{
                        backgroundColor:
                          categoryColors[index % categoryColors.length].bgColor,
                      }}
                      onClick={() => handleCategorySelect(category.name)}
                    >
                      {category.name}
                    </div>
                  ))}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
        <Col md={8} className="mb-4">
          <Card>
            <Card.Body>
              <FormLabel>New task:</FormLabel>
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="New task name"
                  aria-label="New task name"
                  aria-describedby="basic-addon2"
                  value={newTaskInput}
                  onChange={(e) => setNewTaskInput(e.target.value)}
                />
                <InputGroup>
                  <Button variant="outline-secondary" onClick={handleBadge}>
                    <FormOutlined />
                  </Button>
                </InputGroup>
              </InputGroup>
              {isLoadingTasks ? (
                <Spinner animation="border" />
              ) : (
                tasks
                  .filter(
                    (task) =>
                      selectedCategory === "All Tasks" ||
                      task.category === selectedCategory
                  )
                  .map((task) => (
                    <div
                      key={task._id}
                      className={`task-badge ${task.done ? "done" : ""}`}
                      onClick={() => handleBadge(task._id)}
                    >
                      {task.name}
                      <CloseSquareOutlined />
                    </div>
                  ))
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
