import React, { useState } from "react";
import { useQuery, useMutation } from "react-query";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

const TodoList = () => {
  const [categories, setCategories] = useState(["All Tasks"]);
  const [tasks, setTasks] = useState([""])
  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const fetchCategories = useQuery("categories", async () => {
    const response = await API.get("/categories");
    return response.data.categories;
  });

  const fetchTasks = useQuery("tasks", async () => {
    const response = await API.get("/tasks");
    return response.data.tasks;
  });

  console.log(re);

  const createCategoryMutation = useMutation(
    async (name) => {
      const response = await API.post("/category", { name });
      return response.data.category;
    },
    {
      onSuccess: (category) => {
        setCategories((categories) => [...categories, category.name]);
        setSelectedCategory(category.name);
        setNewCategoryInput("");
      },
    }
  );

  const deleteCategoryMutation = useMutation(
    async (name) => {
      await API.delete(`/category/${name}`);
      return name;
    },
    {
      onSuccess: (name) => {
        setCategories((categories) => categories.filter((c) => c !== name));
      },
    }
  );

  const addTaskMutation = useMutation(
    async (task) => {
      const response = await API.post("/task", task);
      return response.data.task;
    },
    {
      onSuccess: (task) => {
        setNewTaskInput("");
      },
    }
  );

  const updateTaskMutation = useMutation(async ({ id, text }) => {
    const response = await API.patch(`/task/${id}`, { text });
    return response.data.task;
  });

  const deleteTaskMutation = useMutation(async (id) => {
    await API.delete(`/task/${id}`);
    return id;
  });

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      createCategoryMutation.mutate(newCategory);
    }
  };

  const handleCategoryDelete = (category) => {
    deleteCategoryMutation.mutate(category);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = (id) => {
    deleteTaskMutation.mutate(id);
  };

  const handleTaskUpdate = (id, newText) => {
    updateTaskMutation.mutate({ id, text: newText });
  };

  const handleBadge = (task) => {
    const existingTaskIndex = tasks.findIndex((t) => t._id === task._id);
    if (existingTaskIndex >= 0) {
      const updatedTask = {
        ...tasks[existingTaskIndex],
        done: !tasks[existingTaskIndex].done,
      };
      setTasks((tasks) => [
        ...tasks.slice(0, existingTaskIndex),
        updatedTask,
        ...tasks.slice(existingTaskIndex + 1),
      ]);
    } else {
      const newTask = { ...task, category: selectedCategory, done: false };
      addTaskMutation.mutate(newTask);
    }

    return (
      <Container>
        <Row className="my-3">
          <Col>
            <FormLabel>Category</FormLabel>
            <InputGroup>
              <FormControl
                value={newCategoryInput}
                onChange={(e) => setNewCategoryInput(e.target.value)}
                placeholder="New category"
              />
              <Button variant="primary" onClick={handleCategoryAdd}>
                Add
              </Button>
            </InputGroup>
          </Col>
          <Col>
            <FormLabel>Tasks</FormLabel>
            <InputGroup>
              <FormControl
                value={newTaskInput}
                onChange={(e) => setNewTaskInput(e.target.value)}
                placeholder="New task"
              />
              <Button
                variant="primary"
                onClick={() => handleBadge({ text: newTaskInput })}
              >
                Add
              </Button>
            </InputGroup>
          </Col>
        </Row>
        <Row className="my-3">
          <Col>
            {fetchCategories.isLoading ? (
              <p>Loading categories...</p>
            ) : fetchCategories.isError ? (
              <p>Error loading categories</p>
            ) : (
              <>
                <FormCheck
                  type="radio"
                  name="category"
                  id="All Tasks"
                  checked={selectedCategory === "All Tasks"}
                  onChange={() => handleCategoryChange("All Tasks")}
                  label="All Tasks"
                />
                {categories.map((category) => (
                  <div key={category}>
                    <FormCheck
                      type="radio"
                      name="category"
                      id={category}
                      checked={selectedCategory === category}
                      onChange={() => handleCategoryChange(category)}
                      label={category}
                    />
                    <Button
                      variant="link"
                      onClick={() => handleCategoryDelete(category)}
                    >
                      <DeleteOutlined />
                    </Button>
                  </div>
                ))}
              </>
            )}
          </Col>
          <Col>
            {fetchTasks.isLoading ? (
              <p>Loading tasks...</p>
            ) : fetchTasks.isError ? (
              <p>Error loading tasks</p>
            ) : (
              <>
                {fetchTasks.data
                  .filter(
                    (task) =>
                      selectedCategory === "All Tasks" ||
                      task.category === selectedCategory
                  )
                  .map((task) => (
                    <Card key={task._id} className="my-3">
                      <Card.Body>
                        <FormCheck
                          type="checkbox"
                          label={task.text}
                          checked={task.done}
                          onChange={() => handleBadge(task)}
                        />
                        <Button
                          variant="link"
                          onClick={() => handleTaskDelete(task._id)}
                        >
                          <DeleteOutlined />
                        </Button>
                        <FormOutlined
                          className="ml-2"
                          onClick={() => handleTaskUpdate(task._id, task.text)}
                        />
                      </Card.Body>
                    </Card>
                  ))}
              </>
            )}
          </Col>
        </Row>
      </Container>
    );
  };
};

export default TodoList;
