import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  FormSelect,
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
  Spinner,
} from "react-bootstrap";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const { data: categoriesData, isLoading: categoriesLoading } = useQuery(
    "categories",
    async () => {
      const response = await API.get("/categories");
      return response.data.categories;
    }
  );

  const { data: tasksData, isLoading: tasksLoading } = useQuery(
    ["tasks", selectedCategory],
    async () => {
      const response = await API.get(
        `/tasks?category=${selectedCategory.toLowerCase()}`
      );
      return response.data.tasks;
    }
  );

  const addCategoryMutation = useMutation(
    async () => {
      const response = await API.post("/category", {
        name: newCategoryInput,
      });
      return response.data.category;
    },
    {
      onSuccess: (data) => {
        setCategories((categories) => [...categories, data]);
        setSelectedCategory(data.name);
        setNewCategoryInput("");
      },
    }
  );

  const addTaskMutation = useMutation(
    async (newTaskInput) => {
      const response = await API.post("/task", {
        category: selectedCategory.toLowerCase(),
        name: newTaskInput,
      });
      return response.data.task;
    },
    {
      onSuccess: (data) => {
        setTasks((tasks) => [...tasks, data]);
        setNewTaskInput("");
      },
    }
  );

  const handleBadge = (id) => {
    if (tasks.find((task) => task._id === id)) {
      setTasks((tasks) =>
        tasks.map((task) => {
          if (task._id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      addTaskMutation.mutate(newTaskInput);
    }
  };

  const categoryColors = {
    "All Tasks": "primary",
    Favourites: "secondary",
    Groceries: "success",
    Work: "danger",
    Study: "warning",
    Sports: "info",
  };

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      addCategoryMutation.mutate();
    }
  };

  const handleCategoryDelete = async (category) => {
    try {
      await API.delete(`/category/${category._id}`);
      setCategories((categories) =>
        categories.filter((cat) => cat._id !== category._id)
      );
      setSelectedCategory("All Tasks");
    } catch (error) {
      console.log(error);
    }
  };

  const handleTaskDelete = async (task) => {
    try {
      await API.delete(`/task/${task._id}`);
      setTasks((tasks) => tasks.filter((t) => t._id !== task._id));
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    if (categoriesData) {
      setCategories(categoriesData);
    }
  }, [categoriesData]);

  useEffect(() => {
    if (tasksData) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  return (
    <Container className="my-5">
      <Row className="justify-content-center mb-3">
        <h1>Todo List</h1>
      </Row>
      <Row className="justify-content-center mb-3">
        <Col md={6}>
          <InputGroup>
            <FormControl
              placeholder="New task"
              aria-label="New task"
              value={newTaskInput}
              onChange={(e) => setNewTaskInput(e.target.value)}
            />
            <Button
              variant="primary"
              onClick={() => addTaskMutation.mutate(newTaskInput)}
            >
              Add
            </Button>
          </InputGroup>
        </Col>
      </Row>
      <Row className="justify-content-center mb-3">
        <Col md={6}>
          <InputGroup>
            <FormControl
              placeholder="New category"
              aria-label="New category"
              value={newCategoryInput}
              onChange={(e) => setNewCategoryInput(e.target.value)}
            />
            <Button variant="primary" onClick={handleCategoryAdd}>
              Add
            </Button>
          </InputGroup>
        </Col>
      </Row>
      <Row className="justify-content-center mb-3">
        <Col md={6}>
          {categoriesLoading ? (
            <Spinner animation="border" role="status">
              <span className="sr-only">Loading...</span>
            </Spinner>
          ) : (
            <FormSelect
              aria-label="Select category"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              {categories.map((category) => (
                <option key={category._id} value={category.name}>
                  {category.name}
                </option>
              ))}
            </FormSelect>
          )}
        </Col>
      </Row>
      <Row className="justify-content-center mb-3">
        <Col md={6}>
          {tasksLoading ? (
            <Spinner animation="border" role="status">
              <span className="sr-only">Loading...</span>
            </Spinner>
          ) : tasks.length === 0 ? (
            <div className="text-center">No tasks yet</div>
          ) : (
            <div>
              {tasks.map((task) => (
                <Card key={task._id} className="mb-3">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <FormCheck
                        type="checkbox"
                        label={task.name}
                        checked={task.done}
                        onChange={() => handleBadge(task._id)}
                      />
                      <div>
                        <Button
                          variant="light"
                          onClick={() => handleTaskDelete(task)}
                        >
                          <DeleteOutlined />
                        </Button>{" "}
                        <Button variant="light">
                          <FormOutlined />
                        </Button>{" "}
                        <Button variant="light">
                          <CloseSquareOutlined />
                        </Button>
                      </div>
                    </div>
                  </Card.Body>
                  <Card.Footer>
                    <Badge bg={categoryColors[task.category]}>
                      {task.category}
                    </Badge>
                  </Card.Footer>
                </Card>
              ))}
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
}
