import React, { useState } from "react";
import { Alert, Button, Form } from "react-bootstrap";
import { useMutation } from "react-query";
import { API } from "../../config/api";

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);

  const [message, setMessage] = useState(null);

  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
  });

  const { name, email, password } = form;

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleFormSubmit = useMutation(async (e) => {
    try {
      e.preventDefault();
      const response = await API.post("/register", form);
      const alert = <Alert variant="success">Register Success </Alert>;
      setMessage(alert);
      console.log(response);
    } catch (error) {
      console.log(error);
      const alert = <Alert variant="danger">{e.response.data.message}</Alert>;
      setMessage(alert);
    }
  });

  return (
    <div className="d-flex align-items-center min-vh-100">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-20">
            <h1 className="mb-5">Welcome to My App</h1>
            <Form onSubmit={(e) => handleFormSubmit.mutate(e)}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label
                type
                >Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" />
              </Form.Group>
              {isSignUp && (
                <Form.Group className="mb-3" controlId="formBasicName">
                  <Form.Label>Name</Form.Label>
                  <Form.Control type="text" placeholder="Enter your name" />
                </Form.Group>
              )}
              <Button variant="primary" type="submit">
                {isSignUp ? "Sign Up" : "Sign In"}
              </Button>
            </Form>
            <div className="mt-3">
              {isSignUp ? (
                <p>
                  Already have an account?{" "}
                  <Button variant="link" onClick={() => setIsSignUp(false)}>
                    Sign In
                  </Button>
                </p>
              ) : (
                <p>
                  Don't have an account?{" "}
                  <Button variant="link" onClick={() => setIsSignUp(true)}>
                    Sign Up
                  </Button>
                </p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Auth;
