import React, { useState, useEffect } from "react";
import {
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";

import { API, setAuthToken } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newTaskInput, setNewTaskInput] = useState("");
  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");

  const { data: tasksData, isLoading: tasksLoading } = useQuery(
    "tasks",
    async () => {
      const { data } = await API.get("/tasks");
      return data;
    }
  );

  const { data: categoriesData } = useQuery("categories", async () => {
    const { data } = await API.get("/categories");
    return data;
  });

  const [addCategory] = useMutation(
    async (category) => {
      await API.post("/category", { name: category });
      setCategories([...categories, category]);
    },
    {
      onSuccess: () => {
        setNewCategoryInput("");
      },
    }
  );

  const [addTask] = useMutation(
    async (task) => {
      await API.post("/task", task);
      setTasks([...tasks, task]);
    },
    {
      onSuccess: () => {
        setNewTaskInput("");
      },
    }
  );

  const [updateTask] = useMutation(
    async ({ id, text }) => {
      await API.patch(`/task/${id}`, { text });
      setTasks(
        tasks.map((task) => (task._id === id ? { ...task, text } : task))
      );
    }
  );

  const [deleteTask, { isLoading: deleteTaskLoading }] = useMutation(
    async (taskId) => {
      await API.delete(`/task/${taskId}`);
    }
  );

  const handleDeleteTask = (id) => {
    deleteTask(id);
    setTasks(tasks.filter((task) => task._id !== id));
  };

  useEffect(() => {
    if (tasksData) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  useEffect(() => {
    if (categoriesData) {
      setCategories(categoriesData);
    }
  }, [categoriesData]);

  const handleTaskAdd = () => {
    if (!newTaskInput) {
      return;
    }

    const task = {
      text: newTaskInput,
      category: selectedCategory,
      done: false,
    };

    addTask(task);
  };

  const handleTaskUpdate = (id, newText) => {
    if (!newText) {
      return;
    }

    updateTask({ id, text: newText });
  };

  const handleCategoryAdd = () => {
    if (!newCategoryInput) {
      return;
    }

    addCategory(newCategoryInput);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const categoryColor = [    { color: "primary" },    { color: "success" },    { color: "warning" },    { color: "danger" },  ];


    return (
      <Container className="my-5">
        <Row>
          <Col md={4}>
            <Card className="mb-3">
              <Card.Body>
                <Card.Title>Add a Task</Card.Title>
                <FormControl
                  placeholder="Enter task"
                  value={newTaskInput}
                  onChange={(e) => setNewTaskInput(e.target.value)}
                />
                <Dropdown className="my-3">
                  <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                    {selectedCategory}
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                    <Dropdown.Item onClick={() => setSelectedCategory("All Tasks")}>
                      All Tasks
                    </Dropdown.Item>
                    {categories.map((category, index) => (
                      <Dropdown.Item
                        key={category._id}
                        onClick={() => handleCategoryChange(category.name)}
                        className={`text-${categoryColor[index % 4].color}`}
                      >
                        {category.name}
                      </Dropdown.Item>
                    ))}
                  </Dropdown.Menu>
                </Dropdown>
                <Button variant="primary" onClick={handleTaskAdd}>
                  Add Task
                </Button>
              </Card.Body>
            </Card>
            <Card>
              <Card.Body>
                <Card.Title>Add a Category</Card.Title>
                <FormControl
                  placeholder="Enter category name"
                  value={newCategoryInput}
                  onChange={(e) => setNewCategoryInput(e.target.value)}
                />
                <Button variant="primary" onClick={handleCategoryAdd}>
                  Add Category
                </Button>
              </Card.Body>
            </Card>
          </Col>
          <Col md={8}>
            <Card>
              <Card.Body>
                <Card.Title>Tasks</Card.Title>
                {tasksLoading && <Spinner animation="border" />}
                {!tasksLoading && tasks.length === 0 && (
                  <p className="text-muted">No tasks available</p>
                )}
                {!tasksLoading && tasks.length > 0 && (
                  <ListGroup>
                    {tasks
                      .filter(
                        (task) =>
                          selectedCategory === "All Tasks" ||
                          task.category === selectedCategory
                      )
                      .map((task) => (
                        <Task
                          key={task._id}
                          task={task}
                          onUpdate={handleTaskUpdate}
                          onDelete={handleDeleteTask}
                          categoryColor={categoryColor}
                        />
                      ))}
                  </ListGroup>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    );
                };
