import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(["All Tasks"]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const categoryColor = [
    {
      index: 0,
      color: "danger",
    },
    {
      index: 1,
      color: "success",
    },
    {
      index: 2,
      color: "warning",
    },
    {
      index: 3,
      color: "info",
    },
  ];

  const { data: categoriesData } = useQuery("categories", fetchCategories);
  const { data: tasksData } = useQuery("tasks", fetchTasks);

  useEffect(() => {
    if (categoriesData) {
      setCategories(["All Tasks", ...categoriesData]);
    }
  }, [categoriesData]);

  useEffect(() => {
    if (tasksData) {
      setTasks(tasksData);
    }
  }, [tasksData]);

  // const [addTaskMutation] = useMutation(addTask, {
  //   onSuccess: (data) => {
  //     setTasks([...tasks, data]);
  //     setNewTaskInput("");
  //   },
  // });

  const [updateTaskMutation] = useMutation(updateTask, {
    onSuccess: (data) => {
      setTasks(
        tasks.map((task) => {
          if (task._id === data._id) {
            return data;
          }
          return task;
        })
      );
    },
  });

  const [deleteTaskMutation] = useMutation(deleteTask, {
    onSuccess: (data) => {
      setTasks(tasks.filter((task) => task._id !== data._id));
    },
  });

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleBadge = (id) => {
    if (tasks.find((task) => task._id === id)) {
      updateTaskMutation({
        id,
        category: selectedCategory,
        text: newTaskInput,
        done: !tasks.find((task) => task._id === id).done,
      });
    } else {
      addTaskMutation({ category: selectedCategory, text: newTaskInput });
    }
    setNewTaskInput("");
  };

  const handleTaskDelete = (id) => {
    deleteTaskMutation(id);
  };

  const handleTaskUpdate = (task) => {
    updateTaskMutation({
      id: task._id,
      category: task.category,
      text: task.text,
    });
  };

  return (
    <>
      <Container>
        <Row>
          <Col>
            <h1 className="text-center my-5">To Do List</h1>
          </Col>
        </Row>
        <Row className="mt-5">
          <Col md={4}>
            <h4>Add Category</h4>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="New Category"
                aria-label="New Category"
                value={newCategoryInput}
                onChange={(e) => setNewCategoryInput(e.target.value)}
              />
              <Button variant="outline-secondary" onClick={handleCategoryAdd}>
                Add
              </Button>
            </InputGroup>
            {categories.map((category, index) => (
              <Card
                key={index}
                className="mb-3"
                onClick={() => handleCategoryChange(category)}
                bg={
                  selectedCategory === category
                    ? categoryColor[index % 4].color
                    : "light"
                }
                text={
                  selectedCategory === category
                    ? "white"
                    : categoryColor[index % 4].color
                }
              >
                <Card.Body>
                  <div className="d-flex justify-content-between align-items-center">
                    <span>{category}</span>
                    {category !== "All Tasks" && (
                      <CloseSquareOutlined
                        onClick={(e) => {
                          e.stopPropagation();
                          handleCategoryDelete(category);
                        }}
                      />
                    )}
                  </div>
                </Card.Body>
              </Card>
            ))}
          </Col>
          <Col md={8}>
            <h4>Add Task</h4>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="New Task"
                aria-label="New Task"
                value={newTaskInput}
                onChange={(e) => setNewTaskInput(e.target.value)}
              />
              <Button variant="outline-secondary" onClick={() => handleBadge()}>
                Add
              </Button>
            </InputGroup>
            <FormCheck>
              {tasks
                .filter(
                  (task) =>
                    selectedCategory === "All Tasks" ||
                    task.category === selectedCategory
                )
                .map((task) => (
                  <div
                    key={task._id}
                    className="d-flex justify-content-between align-items-center"
                  >
                    <div>
                      <FormCheck.Input
                        type="checkbox"
                        checked={task.done}
                        onChange={() => handleBadge(task._id)}
                      />
                      <span
                        className={`mx-2 ${
                          task.done
                            ? "text-muted text-decoration-line-through"
                            : ""
                        }`}
                      >
                        {task.text}
                      </span>
                    </div>
                    <div>
                      <Button
                        variant="outline-secondary"
                        className="mx-2"
                        onClick={() => handleTaskUpdate(task)}
                      >
                        <FormOutlined />
                      </Button>
                      <Button
                        variant="outline-secondary"
                        onClick={() => handleTaskDelete(task._id)}
                      >
                        <DeleteOutlined />
                      </Button>
                    </div>
                  </div>
                ))}
            </FormCheck>
          </Col>
        </Row>
      </Container>
    </>
  );
}

async function fetchCategories() {
  try {
    const response = await API.get("/categories");
    return response.data.data.categories;
  } catch (error) {
    console.log(error);
  }
}

async function fetchTasks() {
  try {
    const response = await API.get("/tasks");
    return response.data.data.tasks;
  } catch (error) {
    console.log(error);
  }
}

async function addTask(task) {
  try {
    const response = await API.post("/tasks", task);
    return response.data.data.task;
  } catch (error) {
    console.log(error);
  }
}

async function updateTask(task) {
  try {
    const response = await API.patch(`/task/${task.id}`, {
      category: task.category,
      text: task.text,
      done: task.done,
    });
    return response.data.data.task;
  } catch (error) {
    console.log(error);
  }
}

async function deleteTask(id) {
  try {
    const response = await API.delete(`/tasks/${id}`);
    return response.data.data.task;
  } catch (error) {
    console.log(error);
  }
}

async function addCategory(category) {
  try {
    const response = await API.post("/categories", { category });
    return response.data.data.category;
  } catch (error) {
    console.log(error);
  }
}

async function deleteCategory(category) {
  try {
    const response = await API.delete(`/categories/${category}`);
    return response.data.data.category;
  } catch (error) {
    console.log(error);
  }
}

async function updateCategory(oldCategory, newCategory) {
  try {
    const response = await API.patch(`/categories/${oldCategory}`, {
      category: newCategory,
    });
    return response.data.data.category;
  } catch (error) {
    console.log(error);
  }
}
