import React, { useState, useEffect } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";
import { API } from "../../config/api";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  useEffect(() => {
    const fetchTasks = async () => {
      const response = await API.get("/tasks");
      setTasks(response.data);
    };
    fetchTasks();

    const fetchCategories = async () => {
      const response = await API.get("/categories");
      setCategories(response.data);
    };
    fetchCategories();
  }, []);

  const handleBadge = (id) => {
    if (tasks.find((task) => task.id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task.id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      const newTasks = tasks.concat({
        id,
        category: selectedCategory,
        text: newTaskInput,
        done: false,
      });
      setTasks(newTasks);
      setNewTaskInput("");
    }
  };

  const categoryColor = [
    {
      index: 0,
      bgColor: "danger.500",
    },
    {
      index: 1,
      bgColor: "purple.500",
    },
    {
      index: 2,
      bgColor: "green.500",
    },
    {
      index: 3,
      bgColor: "primary.500",
    },
    {
      index: 4,
      bgColor: "warning.500",
    },
  ];

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = async (_id) => {
    await API.delete(`/task/${_id}`);
    setTasks(tasks.filter((task) => task._id !== _id));
  };

  const handleTaskUpdate = async (id, newText) => {
    await API.patch(`/task/${id}`, { text: newText });
    setTasks(
      tasks.map((task) => {
        if (task.id === id) {
          return { ...task, text: newText };
        }
        return task;
      })
    );
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={5}>
                <div className="text-start m-3">
                  {categories.map((category) => (
                    <div
                      key={category}
                      className="d-flex align-items-center justify-content-between"
                    >
                      <Badge
                        bg="danger"
                        onClick={() => handleCategoryChange(category)}
                        className={
                          category === selectedCategory ? "selected" : ""
                        }
                      >
                        {category}
                      </Badge>
                      <DeleteOutlined
                        className="delete-icon"
                        onClick={() => handleCategoryDelete(category)}
                      />
                    </div>
                  ))}
                  <InputGroup className="m-3">
                    <FormControl
                      placeholder="New Category"
                      value={newCategoryInput}
                      onChange={(e) => setNewCategoryInput(e.target.value)}
                    />
                    <Button onClick={handleCategoryAdd}>Add</Button>
                  </InputGroup>
                </div>
              </Col>
              <Col xs={7}>
                <div className="text-end m-3">
                  <InputGroup>
                    <FormControl
                      placeholder="New Task"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button onClick={() => handleBadge(Date.now())}>
                      Add Task
                    </Button>
                  </InputGroup>
                </div>
              </Col>
            </Row>
          </Container>
          <hr />
          <Container>
            {tasks
              .filter(
                (task) =>
                  selectedCategory === "All Tasks" ||
                  task.category === selectedCategory
              )
              .map((task) => (
                <Card key={task.id} className="m-3">
                  <Card.Body>
                    <Row>
                      <Col xs={1}>
                        <FormCheck
                          checked={task.done}
                          onChange={() => handleBadge(task.id)}
                        />
                      </Col>
                      <Col xs={9}>
                        <FormLabel className={task.done ? "done" : ""}>
                          {task.text}
                        </FormLabel>
                      </Col>
                      <Col xs={2}>
                        <FormOutlined
                          className="edit-icon"
                          onClick={() => handleTaskUpdate(task.id, task.text)}
                        />
                        <CloseSquareOutlined
                          className="delete-icon"
                          onClick={() => handleTaskDelete(task.id)}
                        />
                      </Col>
                    </Row>
                  </Card.Body>
                </Card>
              ))}
          </Container>
        </Card>
      </Container>
    </>
  );
}
