import { useContext, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Routes, Route, useNavigate } from "react-router-dom";
import { API, setAuthToken } from "./config/api";

//page
import Landing from "./pages/Landing";
import Home from "./pages/Home"


//context
import { UserContext } from "./components/contexts/UserContexts";



function App() {
  // Init user context
  const [state, dispatch] = useContext(UserContext);
  const navigate = useNavigate();

   useEffect(() => {
     // Redirect Auth
     if (localStorage.token) {
       setAuthToken(localStorage.token);
     }

     if (state.isLogin == false && !isLoading) {
       navigate("/");
     } else {
       if (state.user.status == "admin") {
         navigate("/complain-admin");
       } else if (state.user.status == "customer") {
         navigate("/");
       }
     }
   }, [state]);

   // Create function for check user token here ...

   // Call function check user with useEffect didMount here ...
   const checkUser = async () => {
     try {
       const response = await API.get("/check-auth");
       // console.log("ni cek auth nayri token", response);

       // If the token incorrect
       if (response.status === 404) {
         return dispatch({
           type: "AUTH_ERROR",
         });
       }

       // Get user data
       let payload = response.data.data;
       // console.log("paylod login", payload);
       // Get token from local storage
       payload.token = localStorage.token;

       // Send data to useContext
       dispatch({
         type: "USER_SUCCESS",
         payload,
       });
       setIsLoading(false);
     } catch (error) {
       console.log(error);
       setIsLoading(false);
     }
   };

   useEffect(() => {
     checkUser();
   }, []);

  return (
    <Routes>
      <Route exact path="/" element={<Landing />} />
      <Route path="/home" element={<Home />} />
    </Routes>
  );
}

export default App;
