import React, { useState } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  FormSelect,
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
  Spinner,
} from "react-bootstrap";
import { API, setAuthToken } from "../../config/api";
import { useQuery, useMutation } from "react-query";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const { data: categoriesData, isLoading: categoriesLoading } = useQuery(
    "categories",
    async () => {
      const response = await API.get("/categories");
      return response.data.data.categories;
    }
  );

  const { data: tasksData, isLoading: tasksLoading } = useQuery(
    ["tasks", selectedCategory],
    async () => {
      const response = await API.get(
        `/tasks?category=${selectedCategory.toLowerCase()}`
      );
      return response.data.data.tasks;
    }
  );

  const addCategoryMutation = useMutation(
    async () => {
      const response = await API.post("/category", {
        name: newCategoryInput,
      });
      return response.data.data.category;
    },
    {
      onSuccess: (data) => {
        setCategories([...categories, data]);
        setSelectedCategory(data.name);
        setNewCategoryInput("");
      },
    }
  );

  const addTaskMutation = useMutation(
    async (newTaskInput) => {
      const response = await API.post("/task", {
        category: selectedCategory.toLowerCase(),
        name: newTaskInput,
      });
      return response.data.data.task;
    },
    {
      onSuccess: (data) => {
        setTasks([...tasks, data]);
        setNewTaskInput("");
      },
    }
  );

  const handleBadge = (id) => {
    if (tasks.find((task) => task.id === id)) {
      setTasks(
        tasks.map((task) => {
          if (task.id === id) {
            return { ...task, done: !task.done };
          }
          return task;
        })
      );
    } else {
      addTaskMutation.mutate(newTaskInput);
    }
  };

  const categoryColors = {
    "All Tasks": "primary",
    Favourites: "secondary",
    Groceries: "success",
    Work: "danger",
    Study: "warning",
    Sports: "info",
  };

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      addCategoryMutation.mutate();
    }
  };

  const handleCategoryDelete = async (category) => {
    try {
      await API.delete(`/categories/${category.id}`);
      setCategories(categories.filter((cat) => cat.id !== category.id));
      setSelectedCategory("All Tasks");
    } catch (err) {
      console.log(err);
    }
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = async (id) => {
    try {
      await API.delete(`/tasks/${id}`);
      setTasks(tasks.filter((task) => task.id !== id));
    } catch (err) {
      console.log(err);
    }
  };

  const handleTaskChange = (id, newName) => {
    setTasks(
      tasks.map((task) => {
        if (task.id === id) {
          return { ...task, name: newName };
        }
        return task;
      })
    );
  };

  return (
    <Container>
      <Row className="justify-content-center mt-5">
        <Col md={6}>
          <Card className="shadow-sm">
            <Card.Body>
              <h2 className="text-center mb-4">Todo App</h2>
              <Row className="mb-3">
                <Col>
                  <InputGroup>
                    <FormControl
                      placeholder="Add Task"
                      aria-label="Add Task"
                      aria-describedby="basic-addon2"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button
                      variant="outline-secondary"
                      id="button-addon2"
                      onClick={() => addTaskMutation.mutate(newTaskInput)}
                    >
                      Add
                    </Button>
                  </InputGroup>
                </Col>
              </Row>
              <Row className="mb-3">
                <Col>
                  <InputGroup>
                    <FormControl
                      placeholder="Add Category"
                      aria-label="Add Category"
                      aria-describedby="basic-addon2"
                      value={newCategoryInput}
                      onChange={(e) => setNewCategoryInput(e.target.value)}
                    />
                    <Button
                      variant="outline-secondary"
                      id="button-addon2"
                      onClick={handleCategoryAdd}
                    >
                      Add
                    </Button>
                  </InputGroup>
                </Col>
              </Row>
              <Row className="mb-3">
                <Col>
                  <FormLabel>Categories</FormLabel>
                  <InputGroup>
                    <FormSelect
                      value={selectedCategory}
                      onChange={(e) => handleCategoryChange(e.target.value)}
                    >
                      {categoriesLoading ? (
                        <option disabled>Loading...</option>
                      ) : (
                        <>
                          <option value="All Tasks">All Tasks</option>
                          {categories.map((category) => (
                            <option key={category.id} value={category.name}>
                              {category.name}
                            </option>
                          ))}
                        </>
                      )}
                    </FormSelect>
                    <Button
                      variant="outline-secondary"
                      id="button-addon2"
                      onClick={() =>
                        handleCategoryDelete({
                          id: selectedCategory.id,
                          name: selectedCategory,
                        })
                      }
                    >
                      <DeleteOutlined />
                    </Button>
                  </InputGroup>
                </Col>
              </Row>
              <Row>
                <Col>
                  {tasksLoading ? (
                    <Spinner animation="border" className="text-center" />
                  ) : (
                    <>
                      {tasks.length === 0 && (
                        <p className="text-center">No tasks found</p>
                      )}
                      {tasks.map((task) => (
                        <Card
                          key={task.id}
                          className="mb-2 shadow-sm"
                          border={
                            categoryColors[task.category]
                              ? categoryColors[task.category]
                              : "dark"
                          }
                        >
                          <Card.Body>
                            <Row>
                              <Col xs={1}>
                                <FormCheck
                                  type="checkbox"
                                  checked={task.done}
                                  onChange={() => handleBadge(task.id)}
                                />
                              </Col>
                              <Col xs={9}>
                                <FormControl
                                  value={task.name}
                                  onChange={(e) =>
                                    handleTaskChange(task.id, e.target.value)
                                  }
                                />
                              </Col>
                            </Row>
                          </Card.Body>
                        </Card>
                      ))}
                    </>
                  )}
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
