import React, { useState } from "react";
import {
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([
    "All Tasks",
    "Favourites",
    "Groceries",
    "Work",
    "Study",
    "Sports",
  ]);
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const handleBadge = (id) => {
    if (tasks.find((task) => task.id === id)) {
      setTasks(tasks.filter((task) => task.id !== id));
    } else {
      const newTasks = tasks.concat({
        id,
        category: selectedCategory,
        text: `Task ${id}`,
      });
      setTasks(newTasks);
    }
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const handleTaskUpdate = (id, newText) => {
    setTasks(
      tasks.map((task) => {
        if (task.id === id) {
          return { ...task, text: newText };
        }
        return task;
      })
    );
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={3}>
                <div className="text-start m-3">
                  {categories.map((category) => (
                    <p
                      key={category}
                      className={selectedCategory === category ? "fw-bold" : ""}
                      onClick={() => handleCategoryChange(category)}
                    >
                      {category}
                    </p>
                  ))}
                  <p className="opacity-50">+ New category</p>
                </div>
              </Col>

              <Col xs={9}>
                <div className="p-3">
                  <p className="text-start">TODO LIST</p>
                  <InputGroup className="mb-2">
                    <FormControl
                      placeholder="Add a new task"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button
                      onClick={() => {
                        const newTasks = tasks.concat({
                          id: Math.random(),
                          category: selectedCategory,
                          text: newTaskInput,
                        });
                        setTasks(newTasks);
                        setNewTaskInput("");
                      }}
                    >
                      Add
                    </Button>
                  </InputGroup>
                </div>
                {tasks
                  .filter(
                    (task) =>
                      selectedCategory === "All Tasks" ||
                      task.category === selectedCategory
                  )
                  .map((task) => (
                    <div
                      key={task.id}
                      className="d-flex px-3 my-2 align-content-center"
                    >
                      <FormCheck
                        className="me-3"
                        onClick={() => handleBadge(task.id)}
                        checked={tasks.find((t) => t.id === task.id)}
                      />
                      <FormControl
                        className="me-2"
                        value={task.text}
                        onChange={(e) =>
                          handleTaskUpdate(task.id, e.target.value)
                        }
                      />
                      <Button
                        size="sm"
                        onClick={() => handleTaskDelete(task.id)}
                      >
                        Delete
                      </Button>
                    </div>
                  ))}
              </Col>
            </Row>
          </Container>
        </Card>
      </Container>
    </>
  );
}
