import React, { useState } from "react";
import {
  CloseSquareOutlined,
  DeleteOutlined,
  FormOutlined,
} from "@ant-design/icons";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  FormCheck,
  FormControl,
  FormLabel,
  InputGroup,
  Row,
} from "react-bootstrap";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([
    "All Tasks",
    "Favourites",
    "Groceries",
    "Work",
    "Study",
    "Sports",
  ]);

  const [newCategoryInput, setNewCategoryInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Tasks");
  const [newTaskInput, setNewTaskInput] = useState("");

  const handleBadge = (id) => {
    if (tasks.find((task) => task.id === id)) {
      setTasks(tasks.filter((task) => task.id !== id));
    } else {
      const newTasks = tasks.concat({
        id,
        category: selectedCategory,
        text: `Task ${id}`,
      });
      setTasks(newTasks);
    }
  };

  const categoryColors = {
    "All Tasks": "primary",
    Favourites: "secondary",
    Groceries: "success",
    Work: "danger",
    Study: "warning",
    Sports: "info",
  };

  const handleCategoryAdd = () => {
    const newCategory = newCategoryInput.trim();
    if (newCategory !== "" && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      setSelectedCategory(newCategory);
      setNewCategoryInput("");
    }
  };

  const handleCategoryDelete = (category) => {
    setCategories(categories.filter((cat) => cat !== category));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleTaskDelete = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const handleTaskUpdate = (id, newText) => {
    setTasks(
      tasks.map((task) => {
        if (task.id === id) {
          return { ...task, text: newText };
        }
        return task;
      })
    );
  };

  return (
    <>
      <Container className="my-5 mx-0 d-flex justify-content-center align-items-center">
        <Card bg="white" style={{ width: "70%" }}>
          <Container className="text-center">
            <Row>
              <Col xs={5}>
                <div className="text-start m-3">
                  {categories.map((category) => (
                    <div key={category}>
                      <p
                        className={
                          selectedCategory === category ? "fw-bold" : ""
                        }
                        onClick={() => handleCategoryChange(category)}
                      >
                        <Badge bg={category.toLowerCase()} className="me-2" />
                        {category}
                        <CloseSquareOutlined
                          onClick={() => handleCategoryDelete(category)}
                        />
                      </p>
                    </div>
                  ))}
                  <div className="d-flex align-items-center">
                    <FormLabel className="opacity-50 me-2">
                      New category:
                    </FormLabel>
                    <InputGroup className="mb-3">
                      <FormControl
                        placeholder="Category name"
                        value={newCategoryInput}
                        onChange={(e) => setNewCategoryInput(e.target.value)}
                      />
                      <FormOutlined onClick={handleCategoryAdd} />
                    </InputGroup>
                  </div>
                </div>
              </Col>

              <Col xs={7}>
                <div className="text-start m-3">
                  <FormLabel className="opacity-50 me-2">New task:</FormLabel>
                  <InputGroup className="mb-3">
                    <FormControl
                      placeholder="Task name"
                      value={newTaskInput}
                      onChange={(e) => setNewTaskInput(e.target.value)}
                    />
                    <Button
                      variant="primary"
                      onClick={() => handleBadge(tasks.length + 1)}
                    >
                      Add Task
                    </Button>
                  </InputGroup>
                  {tasks
  .filter(
    (task) =>
      selectedCategory === "All Tasks" || task.category === selectedCategory
  )
  .map((task) => (
    <div
      key={task.id}
      className="d-flex align-items-center justify-content-between mb-3"
    >
      <div className="d-flex align-items-center">
        <FormCheck
          type="checkbox"
          className="me-3"
          checked={tasks.includes(task)}
          onChange={() => handleBadge(task.id)}
        />
        <Badge bg={categoryColors[task.category]} className="me-2">
          {task.category}
        </Badge>
        <FormControl
          value={task.text}
          onChange={(e) => handleTaskUpdate(task.id, e.target.value)}
        />
      </div>
      <DeleteOutlined onClick={() => handleTaskDelete(task.id)} />
    </div>
  ))}
                </div>
              </Col>
            </Row>
          </Container>
        </Card>
      </Container>
    </>
  );
}
